INSERT INTO `mgy_exam_ques` VALUES (31, '{\"title\":\"护理学的4个基本概念指的是\",\"option\":{\"A\":\"预防、治疗、护理、环境\",\"B\":\"病人、健康、社会、护理\",\"C\":\"人、环境、健康、预防\",\"D\":\"人、环境、健康、护理\",\"E\":\"病人、预防、治疗、护理\"},\"answer\":\"D\",\"desc\":\"现代护理学主要就人、环境、健康、护理等4个概念进行描述，它们是护理学的4个基本概念。\"}');
INSERT INTO `mgy_exam_ques` VALUES (32, '{\"title\":\"下列哪种沟通形式不属于非语言性沟通\",\"option\":{\"A\":\"面部表情\",\"B\":\"手势\",\"C\":\"身体运动\",\"D\":\"身体姿势\",\"E\":\"健康宣教资料\"},\"answer\":\"E\",\"desc\":\"非语言沟通是指不使用语言的交流。因此，面部表情、手势、身体运动、身体姿势都属于非语言性沟通。而语言性沟通则是指用语言进行的交流，包括书面语言和口头语言等不同形式。健康宣教资料正是使用书面语言进行的交流，它属于语言性沟通。\"}');
INSERT INTO `mgy_exam_ques` VALUES (33, '{\"title\":\"护理程序的第一步，评估进行的时间发生在\",\"option\":{\"A\":\"病人入院时\",\"B\":\"病人入院和出院时\",\"C\":\"病人自入院开始直至出院为止\",\"D\":\"病人出院时\",\"E\":\"遵医嘱完成\"},\"answer\":\"C\",\"desc\":\"护理评估是有组织地、系统地收集资料。从护士与病人的首次接触直至病人出院的整个护理过程中，都贯穿着评估。\"}');
INSERT INTO `mgy_exam_ques` VALUES (34, '{\"title\":\"下列哪项为主诉\",\"option\":{\"A\":\"水肿\",\"B\":\"肝肿大\",\"C\":\"疼痛剧烈\",\"D\":\"肺部湿啰音\",\"E\":\"身高1.65m\"},\"answer\":\"C\",\"desc\":\"病人的主诉是病人对其所经历的感觉、思考的问题及担心的内容进行的描述。\"}');
INSERT INTO `mgy_exam_ques` VALUES (35, '{\"title\":\"在陈述护理诊断方式时，以下哪种方式是正确的\",\"option\":{\"A\":\"知识缺乏\",\"B\":\"知识缺乏，特定的\",\"C\":\"知识缺乏与缺乏预防哮喘复发的知识有关\",\"D\":\"知识缺乏与哮喘发作有关\",\"E\":\"知识缺乏，缺乏有关预防哮喘复发的知识\"},\"answer\":\"E\",\"desc\":\"确立知识缺乏的诊断，可陈述“知识缺乏：缺乏……方面的知识”。\"}');
INSERT INTO `mgy_exam_ques` VALUES (36, '{\"title\":\"下列排序哪个是正确的\",\"option\":{\"A\":\"病人首优的护理诊断可以有多个\",\"B\":\"对于某个病人来说，护理诊断的先后次序常常是固定不变的\",\"C\":\"首优的护理诊断解决之后，再解决中优的护理问题\",\"D\":\"现存的护理诊断应该排在有……危险的护理诊断之前\",\"E\":\"次优问题在护理过程中也是应该马上解决的问题\"},\"answer\":\"A\",\"desc\":\"首优问题是指威胁病人生命，需要立即采取行动解决的问题，应排在首位，多是有关生命体征方面的问题。\"}');
INSERT INTO `mgy_exam_ques` VALUES (37, '{\"title\":\"需要病室空气湿度较高的病人是\",\"option\":{\"A\":\"急性胃炎病人\",\"B\":\"急性喉炎病人\",\"C\":\"心肌梗死病人\",\"D\":\"急性胰腺炎病人\",\"E\":\"慢性胆囊炎病人\"},\"answer\":\"B\",\"desc\":\"病室相对湿度在50%～60%为宜。室内湿度过高，空气潮湿，有利于细菌繁殖，同时机体水分蒸发减少，出汗受抑制，病人感到闷热、不适，尿液排泄增加，对心、肾疾患者不利；室内湿度过低，空气干燥，水分蒸发快，导致呼吸道黏膜干燥、咽痛、口渴，对急性喉炎、气管切开和呼吸道感染的病人十分不利。\"}');
INSERT INTO `mgy_exam_ques` VALUES (38, '{\"title\":\"合理的病室环境是\",\"option\":{\"A\":\"婴儿室室温宜在22～24℃\",\"B\":\"室内相对湿度在30%～40%为宜\",\"C\":\"破伤风病人，室内光线应明亮\",\"D\":\"产休室应保暖，不宜开窗\",\"E\":\"气管切开者，室内相对湿度为40%\"},\"answer\":\"A\",\"desc\":\"一般病室的温度以18～22℃为宜；婴儿室室温应稍高，以22～24℃为宜、病室相对湿度50%～60%，湿度过低对气管切开者不利。破伤风病人应避免各种刺激，如光、声等，以避免引起病人抽搐发作，因此光线宜暗。产休室应该适当开窗通风，保持空气清新。\"}');
INSERT INTO `mgy_exam_ques` VALUES (39, '{\"title\":\"昏迷病人从急诊室被送入病室后，值班护士首先应\",\"option\":{\"A\":\"填写各种卡片\",\"B\":\"通知医生、配合抢救、测量生命体征\",\"C\":\"询问病史，评估发病过程\",\"D\":\"通知营养室，准备膳食\",\"E\":\"介绍医院环境\"},\"answer\":\"B\",\"desc\":\"危重病人病情变化快，应该严密监测，并做好配合抢救的准备。\"}');
INSERT INTO `mgy_exam_ques` VALUES (40, '{\"title\":\"病区值班护士接到住院处通知收一位新病人入院时，其床位的准备是\",\"option\":{\"A\":\"在普通病房准备备用床\",\"B\":\"将备用床改为暂空床\",\"C\":\"在急救室准备备用床\",\"D\":\"在急救室将备用床改为暂空床\",\"E\":\"在隔离室将备用床改为暂空床\"},\"answer\":\"B\",\"desc\":\"铺暂空床目的：①保持病室整洁，迎接新病人住院；②供暂时离床活动的病人卧床休息用。\"}');
INSERT INTO `mgy_exam_ques` VALUES (41, '{\"title\":\"一般病床铺橡胶单和中单其上端距床头为\",\"option\":{\"A\":\"20～25cm\",\"B\":\"30～40cm\",\"C\":\"45～50cm\",\"D\":\"55～65cm\",\"E\":\"65～70cm\"},\"answer\":\"C\",\"desc\":\"一般病床铺橡胶单和中单其上端距床头为45～50cm。\"}');
INSERT INTO `mgy_exam_ques` VALUES (42, '{\"title\":\"备用床的目的是\",\"option\":{\"A\":\"准备接收新病人\",\"B\":\"供新入院病人使用\",\"C\":\"使病人安全舒适及预防并发症\",\"D\":\"便于病人住宿\",\"E\":\"便于陪床人员休息\"},\"answer\":\"A\",\"desc\":\"备用床目的：保持病室整洁，准备接收新病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (43, '{\"title\":\"病室空气污浊可导致\",\"option\":{\"A\":\"影响机体散热\",\"B\":\"疲倦、食欲减退、头晕\",\"C\":\"呼吸道黏膜干燥、咽痛、口渴\",\"D\":\"腹痛、腹泻\",\"E\":\"闷热、难受\"},\"answer\":\"B\",\"desc\":\"病室空气污浊可导致疲倦、食欲减退、头晕。\"}');
INSERT INTO `mgy_exam_ques` VALUES (44, '{\"title\":\"病室群体气氛的主要调节者是\",\"option\":{\"A\":\"病员\",\"B\":\"家属\",\"C\":\"医生\",\"D\":\"护士\",\"E\":\"陪人\"},\"answer\":\"D\",\"desc\":\"病室群体气氛的主要调节者是护士。\"}');
INSERT INTO `mgy_exam_ques` VALUES (45, '{\"title\":\"自觉腹痛者就诊于\",\"option\":{\"A\":\"一般门诊\",\"B\":\"心理门诊\",\"C\":\"急诊\",\"D\":\"保健门诊\",\"E\":\"神经专科门诊\"},\"answer\":\"A\",\"desc\":\"自觉腹痛者就诊于一般门诊，用排除法可选出参考答案。\"}');
INSERT INTO `mgy_exam_ques` VALUES (46, '{\"title\":\"麻醉床的目的是\",\"option\":{\"A\":\"准备接收新病人\",\"B\":\"供新入院病人使用\",\"C\":\"使病人安全舒适及预防并发症\",\"D\":\"便于病人住宿\",\"E\":\"便于陪床人员休息\"},\"answer\":\"C\",\"desc\":\"麻醉床的目的是使病人安全舒适及预防并发症。\"}');
INSERT INTO `mgy_exam_ques` VALUES (47, '{\"title\":\"麻醉未清醒的病人，因腺体分泌物增多，为防窒息，应准备\",\"option\":{\"A\":\"吸痰器\",\"B\":\"氧气\",\"C\":\"舌钳\",\"D\":\"治疗碗\",\"E\":\"张口器\"},\"answer\":\"A\",\"desc\":\"麻醉未清醒的病人，因腺体分泌物增多，为防窒息，应准备吸痰器。\"}');
INSERT INTO `mgy_exam_ques` VALUES (48, '{\"title\":\"铺床时移开床旁桌，距床旁为\",\"option\":{\"A\":\"10cm\",\"B\":\"20cm\",\"C\":\"30cm\",\"D\":\"40cm\",\"E\":\"50cm\"},\"answer\":\"B\",\"desc\":\"铺床时移开床旁桌，距床旁约20cm。\"}');
INSERT INTO `mgy_exam_ques` VALUES (49, '{\"title\":\"对门诊就诊的病人首先应实行\",\"option\":{\"A\":\"心理安慰\",\"B\":\"卫生指导\",\"C\":\"预检分诊\",\"D\":\"查阅病案资料\",\"E\":\"健康教育\"},\"answer\":\"C\",\"desc\":\"对门诊就诊的病人首先应实行预检分诊。\"}');
INSERT INTO `mgy_exam_ques` VALUES (50, '{\"title\":\"病室通风的目的，下列何项不妥\",\"option\":{\"A\":\"可减少汗液的蒸发和热的消散\",\"B\":\"使病室空气新鲜\",\"C\":\"使患者舒适、愉快\",\"D\":\"降低空气中微生物浓度\",\"E\":\"降低二氧化碳浓度\"},\"answer\":\"A\",\"desc\":\"空气流通可以调节室内温、湿度，增加氧含量，降低二氧化碳及空气中微生物的密度。为保持空气新鲜，病室应定时开窗通风换气，每次通风30min左右。冬天通风时要注意保暖，避免冷风直吹病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (51, '{\"title\":\"每个病区一般设多少张病床\",\"option\":{\"A\":\"10～20张\",\"B\":\"20～30张\",\"C\":\"30～40张\",\"D\":\"40～50张\",\"E\":\"50～60张\"},\"answer\":\"C\",\"desc\":\"每个病区一般设30～40张病床。\"}');
INSERT INTO `mgy_exam_ques` VALUES (52, '{\"title\":\"诊室工作应是\",\"option\":{\"A\":\"8h工作制\",\"B\":\"24h开放\",\"C\":\"12h工作制\",\"D\":\"中午只休息2h\",\"E\":\"10h工作制\"},\"answer\":\"B\",\"desc\":\"诊室工作应是24h开放。\"}');
INSERT INTO `mgy_exam_ques` VALUES (53, '{\"title\":\"破伤风病室应\",\"option\":{\"A\":\"安静、整洁\",\"B\":\"温度、湿度适宜\",\"C\":\"安静，光线暗淡\",\"D\":\"安静，阳光充足\",\"E\":\"气流通，光线适中\"},\"answer\":\"C\",\"desc\":\"破伤风病室应安静，光线暗淡。\"}');
INSERT INTO `mgy_exam_ques` VALUES (54, '{\"title\":\"为保持病室空气新鲜，每天开窗通风时间为\",\"option\":{\"A\":\"4h\",\"B\":\"3h\",\"C\":\"2h\",\"D\":\"30min\",\"E\":\"10min\"},\"answer\":\"D\",\"desc\":\"为保持病室空气新鲜，每天开窗通风时间为30分钟。\"}');
INSERT INTO `mgy_exam_ques` VALUES (55, '{\"title\":\"铺麻醉床时下列操作错误的是\",\"option\":{\"A\":\"按使用顺序准备好用物\",\"B\":\"按要求将橡胶单、中单铺于床及手术部位\",\"C\":\"盖被纵向三折于门同侧床边\",\"D\":\"枕头横立于床头\",\"E\":\"床旁椅置于门对侧床边\"},\"answer\":\"C\",\"desc\":\"铺麻醉床时盖被纵向三折于门对侧床边。\"}');
INSERT INTO `mgy_exam_ques` VALUES (56, '{\"title\":\"抢救时间的记录不包括\",\"option\":{\"A\":\"病人到达的时间\",\"B\":\"医生到达的时间\",\"C\":\"抢救措施落实的时间\",\"D\":\"家属到达的时间\",\"E\":\"病情变化的时间\"},\"answer\":\"D\",\"desc\":\"抢救时间的记录不包括家属到达的时间。\"}');
INSERT INTO `mgy_exam_ques` VALUES (57, '{\"title\":\"暂空床的目的是\",\"option\":{\"A\":\"准备接收新病人\",\"B\":\"供新入院病人使用\",\"C\":\"使病人安全舒适及预防并发症\",\"D\":\"便于病人住宿\",\"E\":\"便于陪床人员休息\"},\"answer\":\"B\",\"desc\":\"暂空床的目的是供新入院病人使用。\"}');
INSERT INTO `mgy_exam_ques` VALUES (58, '{\"title\":\"医院的任务不包括下列哪项\",\"option\":{\"A\":\"医疗\",\"B\":\"教学\",\"C\":\"科研\",\"D\":\"预防保健\",\"E\":\"制定卫生方针\"},\"answer\":\"E\",\"desc\":\"制定卫生方针不是医院的任务。\"}');
INSERT INTO `mgy_exam_ques` VALUES (59, '{\"title\":\"休克病人入病室后护士首先应\",\"option\":{\"A\":\"填写各种卡片\",\"B\":\"通知医生、配合抢救、测量生命体征\",\"C\":\"询问病史，评估发病过程\",\"D\":\"通知营养室，准备膳食\",\"E\":\"介绍病室病友\"},\"answer\":\"B\",\"desc\":\"危重病人病情变化快，应该严密监测，并做好配合抢救的准备。\"}');
INSERT INTO `mgy_exam_ques` VALUES (60, '{\"title\":\"以下哪些病人应进行二级护理\",\"option\":{\"A\":\"器官移植、大面积烧伤\",\"B\":\"高热、大出血\",\"C\":\"休克、瘫痪\",\"D\":\"年老体弱、幼儿\",\"E\":\"疾病恢复期、选择性手术前的准备阶段\"},\"answer\":\"D\",\"desc\":\"二级护理适用于大手术后病情稳定者，年老体弱、慢性病不宜多活动者，幼儿等。三级护理适用于一般慢性病、疾病恢复期及选择手术前的准备阶段等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (61, '{\"title\":\"特级护理的病人正确的观察时间是\",\"option\":{\"A\":\"24h专人守护\",\"B\":\"应该是15～30min观察一次\",\"C\":\"应该是1～2h观察一次\",\"D\":\"应该是2～3h巡视一次\",\"E\":\"应该是4～6h巡视一次\"},\"answer\":\"A\",\"desc\":\"特级护理适用于严重创伤、复杂疑难的手术后，器官移植，大面积灼伤以及某些严重的内科疾患等。护理内容：①专人24h护理，严密观察病情及生命体征。②制订护理计划，严格执行各项诊疗和护理措施，及时填写特别护理记录单。③备好急救所需药品和用物。④做好基础护理，严防并发症。\"}');
INSERT INTO `mgy_exam_ques` VALUES (62, '{\"title\":\"单人搬运法适合于\",\"option\":{\"A\":\"体重较重者\",\"B\":\"小儿及体重轻者\",\"C\":\"老年人\",\"D\":\"颅脑损伤者\",\"E\":\"腿部骨折者\"},\"answer\":\"B\",\"desc\":\"单人搬运法适合于搬运小儿及体重轻的患者。\"}');
INSERT INTO `mgy_exam_ques` VALUES (63, '{\"title\":\"腰椎骨折病人需用何种方法搬运\",\"option\":{\"A\":\"一人法\",\"B\":\"二人法\",\"C\":\"三人法\",\"D\":\"四人法\",\"E\":\"挪动法\"},\"answer\":\"D\",\"desc\":\"四人搬运法适用于颈椎、腰椎骨折病人或病情较重的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (64, '{\"title\":\"两人搬运病人的正确方法是\",\"option\":{\"A\":\"甲托颈、背部，乙托臀、膝部\",\"B\":\"甲托背部，乙托臀、膝部\",\"C\":\"甲托头、颈、肩、胸部，乙托小腿和大腿\",\"D\":\"甲托头、颈、腰部，乙托小腿和大腿\",\"E\":\"甲托头、颈、肩、腰部，乙托臀、膝部\"},\"answer\":\"E\",\"desc\":\"甲一手抬起病人头、颈、肩部，一手抬起腰部；乙一手抬起病人臀部，一手抬起病人膝部（腘窝处）。\"}');
INSERT INTO `mgy_exam_ques` VALUES (65, '{\"title\":\"某患者被人搀扶着步入医院，接诊护士看见其面色发绀，口唇呈黑紫色，呼吸困难，询问病史得知其有慢性阻塞性肺病史。护士需立即对其采取的措施是\",\"option\":{\"A\":\"分诊协助其就医\",\"B\":\"不处理，静候医生\",\"C\":\"鼻塞法吸氧\",\"D\":\"电击除颤\",\"E\":\"CPR\"},\"answer\":\"C\",\"desc\":\"该患者目前处于重度缺氧状态，应立即进行吸氧治疗。\"}');
INSERT INTO `mgy_exam_ques` VALUES (66, '{\"title\":\"某患者被人搀扶着步入医院，接诊护士看见其面色发绀，口唇呈黑紫色，呼吸困难，询问病史得知其有慢性阻塞性肺病史。护士给予吸氧治疗时应特别注意\",\"option\":{\"A\":\"对患者实施呼吸道隔离\",\"B\":\"让患者保持镇静\",\"C\":\"氧流量1～2L/min\",\"D\":\"只能除颤1次\",\"E\":\"人工呼吸于胸外心脏按压比例为2：30\"},\"answer\":\"C\",\"desc\":\"慢性阻塞性肺病史病人应给予低流量、低浓度吸氧。\"}');
INSERT INTO `mgy_exam_ques` VALUES (67, '{\"title\":\"全身麻醉未清醒的病人采用去枕仰卧位的目的是\",\"option\":{\"A\":\"有利于静脉回流\",\"B\":\"防止颅内压降低\",\"C\":\"防止呕吐物流入气管\",\"D\":\"减轻伤口疼痛\",\"E\":\"减少局部出血\"},\"answer\":\"C\",\"desc\":\"全身麻醉后病人可能出现呕吐，采用去枕仰卧位可使声门高于食管入口，防止呕吐物流入气管引起窒息。\"}');
INSERT INTO `mgy_exam_ques` VALUES (68, '{\"title\":\"对于一个椎管内麻醉的病人，让病人采取去枕仰卧位的主要目的是\",\"option\":{\"A\":\"预防窒息\",\"B\":\"预防肺部感染\",\"C\":\"预防颅压升高\",\"D\":\"预防头疼\",\"E\":\"预防病人的脑水肿\"},\"answer\":\"D\",\"desc\":\"去枕仰卧位适用范围：①昏迷或全身麻醉未清醒等病人，可防止呕吐物流入气管而引起窒息及肺部并发症；②椎管内麻醉或脊髓腔穿刺后6～8h的病人，以预防脑压减低而引起的头痛（穿刺后，脑脊液可自穿刺处漏出至脊膜腔外，造成脑压过低，牵张颅内静脉窦和脑膜等组织而引起）。\"}');
INSERT INTO `mgy_exam_ques` VALUES (69, '{\"title\":\"颅内手术后，头部翻转过剧可引起\",\"option\":{\"A\":\"脑疝\",\"B\":\"休克\",\"C\":\"脑出血\",\"D\":\"脑栓塞\",\"E\":\"脑干损伤\"},\"answer\":\"A\",\"desc\":\"颅内手术后，头部翻转过剧可引起脑疝。\"}');
INSERT INTO `mgy_exam_ques` VALUES (70, '{\"title\":\"下列选项中不属于约束带应观察的项目是\",\"option\":{\"A\":\"衬垫是否垫好\",\"B\":\"约束带是否牢靠\",\"C\":\"体位是否舒适\",\"D\":\"局部皮肤颜色及温度\",\"E\":\"神智是否清楚\"},\"answer\":\"E\",\"desc\":\"垫好衬垫，保持被约束肢体处于功能位，可减轻因约束给病人带来的不适。检查约束带是否牢靠，可增加安全性；观察局部皮肤的颜色和温度，可及时了解病人被约束部位的血液循环情况，避免因约束带造成局部血液循环障碍。\"}');
INSERT INTO `mgy_exam_ques` VALUES (71, '{\"title\":\"一位刚入院的腹痛待查患者，医生给他做腹部检查时，护士应协助病人取哪种卧位最佳\",\"option\":{\"A\":\"截石位\",\"B\":\"去枕平卧\",\"C\":\"俯卧位\",\"D\":\"屈膝仰卧位\",\"E\":\"侧卧位\"},\"answer\":\"D\",\"desc\":\"屈膝仰卧位适用范围：①腹部检查的病人，腹肌放松，利于检查；②导尿的病人，利于暴露操作部位。\"}');
INSERT INTO `mgy_exam_ques` VALUES (72, '{\"title\":\"脑室引流和颅骨牵引时应取\",\"option\":{\"A\":\"侧卧位\",\"B\":\"俯卧位\",\"C\":\"膝胸卧位\",\"D\":\"截石位\",\"E\":\"头高足低位\"},\"answer\":\"E\",\"desc\":\"头高足低位适用范围：①颈椎骨折进行颅骨牵引时做反牵引力；②预防脑水肿，减轻颅内压。开颅手术后，也常取此卧位。\"}');
INSERT INTO `mgy_exam_ques` VALUES (73, '{\"title\":\"女病人导尿时需采取\",\"option\":{\"A\":\"仰卧位\",\"B\":\"侧卧位\",\"C\":\"俯卧位\",\"D\":\"仰卧屈膝位\",\"E\":\"膝胸位\"},\"answer\":\"D\",\"desc\":\"屈膝仰卧位适用范围：①腹部检查的病人，腹肌放松，利于检查；②导尿的病人．利于暴露操作部位。\"}');
INSERT INTO `mgy_exam_ques` VALUES (74, '{\"title\":\"采用被动卧位的病人是\",\"option\":{\"A\":\"心包积液病人\",\"B\":\"心力衰竭病人\",\"C\":\"昏迷病人\",\"D\":\"支气管哮喘病人\",\"E\":\"胸膜炎病人\"},\"answer\":\"C\",\"desc\":\"被动卧位：病人自身无改变卧位的能力，躺在被安置的卧位。\"}');
INSERT INTO `mgy_exam_ques` VALUES (75, '{\"title\":\"阿米巴痢疾病变在回盲部，药物灌肠时可采取\",\"option\":{\"A\":\"左侧卧位，保留灌肠\",\"B\":\"右侧卧位，保留灌肠\",\"C\":\"左侧卧位，小量不保留灌肠\",\"D\":\"右侧卧位，小量不保留灌肠\",\"E\":\"右侧卧位，大量不保留灌肠\"},\"answer\":\"B\",\"desc\":\"阿米巴痢疾病变在回盲部，因此药物灌肠时应采取右侧卧位，保留灌肠。\"}');
INSERT INTO `mgy_exam_ques` VALUES (76, '{\"title\":\"自己不能活动的病员，护士扶助移向床头需要用\",\"option\":{\"A\":\"一人法\",\"B\":\"二人法\",\"C\":\"三人法\",\"D\":\"四人法\",\"E\":\"慢慢移动\"},\"answer\":\"B\",\"desc\":\"自己不能活动的病员，护士扶助移向床头需要用2人法。\"}');
INSERT INTO `mgy_exam_ques` VALUES (77, '{\"title\":\"书写病区报告的顺序是先写\",\"option\":{\"A\":\"施行手术病人\",\"B\":\"转入病人\",\"C\":\"危重病人\",\"D\":\"新入院病人\",\"E\":\"转出病人\"},\"answer\":\"E\",\"desc\":\"书写病区报告的顺序：当日离去病室的病人、进入病室的新病人、病室内需重点护理的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (78, '{\"title\":\"吸氧过程中需要调节氧流量时，正确的做法是\",\"option\":{\"A\":\"先关总开关，再调氧流量\",\"B\":\"先关流量表，再调氧流量\",\"C\":\"先拔出吸氧管，再调氧流量\",\"D\":\"先拔出氧气连接管，再调氧流量\",\"E\":\"先分离吸氧管与氧气连接管，再调氧流量\"},\"answer\":\"E\",\"desc\":\"吸氧过程中需要调节氧流量时，应先分离吸氧管与氧气连接管，再调氧流量，避免损伤肺组织。\"}');
INSERT INTO `mgy_exam_ques` VALUES (79, '{\"title\":\"正常成人呼吸的频率为\",\"option\":{\"A\":\"10～14次/min\",\"B\":\"16～20次/min\",\"C\":\"20～24次/min\",\"D\":\"8～10次/min\",\"E\":\"24～26次/min\"},\"answer\":\"B\",\"desc\":\"正常成人呼吸的频率为16～20次/min。\"}');
INSERT INTO `mgy_exam_ques` VALUES (80, '{\"title\":\"下列属于长期备用医嘱的是\",\"option\":{\"A\":\"一级护理\",\"B\":\"可待因30mgq8hprn\",\"C\":\"普食\",\"D\":\"氧气吸入\",\"E\":\"青霉素80万Uimq6h\"},\"answer\":\"B\",\"desc\":\"长期备用医嘱：有效时间在24h以上，必要时用，由医生注明停止时间后失效。\"}');
INSERT INTO `mgy_exam_ques` VALUES (81, '{\"title\":\"不需要去枕仰卧位的病人有\",\"option\":{\"A\":\"昏迷者\",\"B\":\"全身麻醉未清醒者\",\"C\":\"脊椎麻醉者\",\"D\":\"背腰、臀部有伤口者\",\"E\":\"脊髓腔穿刺后\"},\"answer\":\"D\",\"desc\":\"俯卧位适用范围：①腰、背部手术或检查；②腰、背、臀部有伤口，不能平卧或侧卧的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (82, '{\"title\":\"胎膜早破的孕妇应采用的卧位是\",\"option\":{\"A\":\"屈膝仰卧位\",\"B\":\"头低足高位\",\"C\":\"头高足低位\",\"D\":\"侧卧位\",\"E\":\"截石位\"},\"answer\":\"B\",\"desc\":\"头低足高位适用范围：①肺部分泌物引流，使痰易于咳出；②十二指肠引流，有利于胆汁引流；③妊娠时胎膜早破，防止脐带脱垂；④跟骨牵引或胫骨牵引时，利用人体重力作反牵引力，防止下滑。\"}');
INSERT INTO `mgy_exam_ques` VALUES (83, '{\"title\":\"心包填塞病人应采取\",\"option\":{\"A\":\"侧卧位\",\"B\":\"仰卧位\",\"C\":\"仰卧屈膝位\",\"D\":\"平卧高枕位\",\"E\":\"端坐位\"},\"answer\":\"E\",\"desc\":\"端坐位适用范围：急性肺水肿、心包积液及支气管哮喘发作时，由于极度呼吸困难，病人被迫端坐。\"}');
INSERT INTO `mgy_exam_ques` VALUES (84, '{\"title\":\"大量不保留灌肠应采取\",\"option\":{\"A\":\"仰卧位\",\"B\":\"端坐位\",\"C\":\"半卧位\",\"D\":\"俯卧位\",\"E\":\"侧卧位\"},\"answer\":\"E\",\"desc\":\"侧卧位适用范围：灌肠、肛门检查、臀部肌内注射、配合胃镜检查等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (85, '{\"title\":\"胃切除术后患者取半坐卧位的目的是\",\"option\":{\"A\":\"减少局部出血\",\"B\":\"使静脉回流量减少\",\"C\":\"减轻肺部淤血\",\"D\":\"减少炎症的扩散和毒素吸收\",\"E\":\"减轻伤口缝合处的张力\"},\"answer\":\"E\",\"desc\":\"胃切除术后患者取半坐卧位的目的是减轻伤口缝合处的张力。\"}');
INSERT INTO `mgy_exam_ques` VALUES (86, '{\"title\":\"腰穿病人术后6h去枕平卧的目的\",\"option\":{\"A\":\"防止血压下降引起休克\",\"B\":\"防止脑缺血\",\"C\":\"防止术后头痛\",\"D\":\"有利于脑部血循环\",\"E\":\"防止呕吐物引起窒息\"},\"answer\":\"C\",\"desc\":\"腰穿病人术后6h去枕平卧的目的是防止术后头痛。\"}');
INSERT INTO `mgy_exam_ques` VALUES (87, '{\"title\":\"某病人48岁，1个月来大便带血，消瘦，拟行直肠镜检查，检查时应取的卧位是\",\"option\":{\"A\":\"左侧卧位\",\"B\":\"屈膝仰卧位\",\"C\":\"截石位\",\"D\":\"膝胸位\",\"E\":\"蹲位\"},\"answer\":\"D\",\"desc\":\"膝胸位适用范围：①用于肛门、直肠、乙状结肠的检查及治疗；②用于矫正子宫后倾及胎位不正。\"}');
INSERT INTO `mgy_exam_ques` VALUES (88, '{\"title\":\"某病人因车祸脾破裂急诊入院，出现胸闷、气促、出冷汗、脉细速，血压9.1/6.7kPa。护士应立即为其安置\",\"option\":{\"A\":\"平卧位\",\"B\":\"中凹卧位\",\"C\":\"侧卧位\",\"D\":\"俯卧位\",\"E\":\"头低足高位\"},\"answer\":\"B\",\"desc\":\"中凹卧位适用范围：休克病人。抬高头胸部，有利于呼吸；抬高下肢，有利于静脉血回流，增加心输出量。\"}');
INSERT INTO `mgy_exam_ques` VALUES (89, '{\"title\":\"若该女性自然分娩，可采用\",\"option\":{\"A\":\"去枕仰卧位\",\"B\":\"头高足低位\",\"C\":\"头低足高位\",\"D\":\"膝胸位\",\"E\":\"截石位\"},\"answer\":\"E\",\"desc\":\"产妇分娩时采用截石位。\"}');
INSERT INTO `mgy_exam_ques` VALUES (90, '{\"title\":\"若女性怀孕34周时发生胎膜早破，为防止脐带脱垂，应采用\",\"option\":{\"A\":\"截石位\",\"B\":\"膝胸位\",\"C\":\"头低足高位\",\"D\":\"头高足低位\",\"E\":\"去枕仰卧位\"},\"answer\":\"C\",\"desc\":\"妊娠时胎膜早破应采用头低足高位，防止脐带脱垂。\"}');
INSERT INTO `mgy_exam_ques` VALUES (91, '{\"title\":\"产后为促进子宫复原，可采用\",\"option\":{\"A\":\"截石位\",\"B\":\"膝胸位\",\"C\":\"头低足高位\",\"D\":\"头高足低位\",\"E\":\"去枕仰卧位\"},\"answer\":\"B\",\"desc\":\"产后采用膝胸位可促进子宫复原。\"}');
INSERT INTO `mgy_exam_ques` VALUES (92, '{\"title\":\"为预防褥疮，可选用哪种浴液局部按摩\",\"option\":{\"A\":\"75%醇\",\"B\":\"0.5%过氧乙酸\",\"C\":\"0.1%醋酸\",\"D\":\"50%乙醇\",\"E\":\"0.1%氯化钠\"},\"answer\":\"D\",\"desc\":\"为预防褥疮，增进局部血液循环，经常查看受压部位，定期用50%乙醇或红花酒精按摩。\"}');
INSERT INTO `mgy_exam_ques` VALUES (93, '{\"title\":\"危重病人最佳的晨间护理程序是\",\"option\":{\"A\":\"口腔护理-皮肤护理-给予大小便器-扫床或换床单\",\"B\":\"口腔护理-皮肤护理-扫床或换床单-给予大小便器\",\"C\":\"给予大小便器-扫床或换床单-口腔护理-皮肤护理\",\"D\":\"给予大小便器-口腔护理-皮肤护理-扫床或更换床单\",\"E\":\"使用大小便器-皮肤护理-口腔护理-扫床或更换床单\"},\"answer\":\"D\",\"desc\":\"危重病人晨间护理应首先进行排便护理。\"}');
INSERT INTO `mgy_exam_ques` VALUES (94, '{\"title\":\"灭头虱的方法中，错误的一项是\",\"option\":{\"A\":\"用纱布蘸百部酊，按顺序擦遍头发\",\"B\":\"反复揉搓头发10min\",\"C\":\"24h后用篦子篦去死虱，洗发\",\"D\":\"病人换下污衣裤高压蒸汽灭菌\",\"E\":\"梳子和篦子先清洗后消毒\"},\"answer\":\"E\",\"desc\":\"灭头虱操作方法：穿隔离衣，戴手套，将用品携至床旁，向病人解释以取得病人的合作；必要时，先动员病人将头发剪短，剪下的头发用纸包裹焚烧；用纱布蘸百部酊将头发分层擦遍，使其全部湿透，反复揉搓10min，然后给病人戴好帽子包严头发；24h后，取下帽子，用篦子篦去死虱和虮；清洗头发；为病人更换被服及衣裤。\"}');
INSERT INTO `mgy_exam_ques` VALUES (95, '{\"title\":\"关于高压蒸汽灭菌，不正确的是\",\"option\":{\"A\":\"无菌包不宜过大\",\"B\":\"放置无菌包不宜太\",\"C\":\"布类物品放在搪瓷物品之下\",\"D\":\"高压锅内物品不能装得太多\",\"E\":\"放置时各包之间要有空隙\"},\"answer\":\"C\",\"desc\":\"高压蒸汽灭菌注意事项：①清洗灭菌前应将物品彻底清洗干净，物品洗涤后，应干燥并及时包装。②灭菌包体积不得超过30cm×30cm×25cm；用于预真空和脉动真空压力蒸汽灭菌器的物品包，体积不得超过30cm×30cm×50cm。③物品装放时，相互间均应间隔一定距离以利蒸汽置换空气。④布类放在金属、搪瓷物品之上。难于灭菌的大包放在上层，较易灭菌的小包放在下层；金属物品放下层，织物包放上层，金属包的重量不超过7kg，敷料包不超过5kg。⑤随时观察压力、温度情况。⑥灭菌物品干燥后方可取出。⑦定期监测灭菌效果。灭菌效果监测方法：①物理监测法，可用留点温度计放入灭菌包内，灭菌后检查读数是否达到灭菌效果。②化学监测法，通过观察化学指示剂、化学指示胶带颜色的改变来进行。③生物监测法，生物监测法是最可靠的监测法。\"}');
INSERT INTO `mgy_exam_ques` VALUES (96, '{\"title\":\"用食醋加热熏蒸主要是改变细菌的\",\"option\":{\"A\":\"蛋白质结构\",\"B\":\"细菌膜渗透性\",\"C\":\"酶的活性\",\"D\":\"代谢功能\",\"E\":\"酸碱环境\"},\"answer\":\"E\",\"desc\":\"用食醋加热熏蒸主要是改变细菌的酸碱环境。\"}');
INSERT INTO `mgy_exam_ques` VALUES (97, '{\"title\":\"漂白粉溶液用于浸泡餐具、便具时，其浓度为\",\"option\":{\"A\":\"0.2%\",\"B\":\"0.3%\",\"C\":\"0.5%\",\"D\":\"0.8%\",\"E\":\"1%\"},\"answer\":\"C\",\"desc\":\"0.5%漂白粉溶液或0.5%～1%氯胺溶液用于消毒餐具、便器等，浸泡30min。\"}');
INSERT INTO `mgy_exam_ques` VALUES (98, '{\"title\":\"流行性出血热病人死亡不应\",\"option\":{\"A\":\"将其个人衣服、被单消毒后交其家属\",\"B\":\"将其使用过的钱、票消毒后交其家属\",\"C\":\"用无菌棉花塞住各孔道\",\"D\":\"用1%氯胺液浸泡过的尸单包裹尸体\",\"E\":\"床单位做好消毒处理\"},\"answer\":\"C\",\"desc\":\"流行性出血热病人死亡，应将其个人衣服、被单消毒后交其家属，将其使用过的钱、票消毒后交其家属，用1%氯胺液浸泡过的尸单包裹尸体，床单位做好消毒处理。\"}');
INSERT INTO `mgy_exam_ques` VALUES (99, '{\"title\":\"流感病人出院后病室空气消毒，常用消毒剂为\",\"option\":{\"A\":\"食醋\",\"B\":\"苯扎溴铵\",\"C\":\"酒精\",\"D\":\"氯胺\",\"E\":\"漂白粉\"},\"answer\":\"A\",\"desc\":\"流感病人出院后病室空气消毒，常用消毒剂为食醋。\"}');
INSERT INTO `mgy_exam_ques` VALUES (100, '{\"title\":\"过氧乙酸溶液用于餐具消毒，其浓度及浸泡时间为\",\"option\":{\"A\":\"0.2%，2min\",\"B\":\"0.2%，10min\",\"C\":\"0.2%～0.5%，10min\",\"D\":\"0.5%，30～60min\",\"E\":\"1%，60min\"},\"answer\":\"D\",\"desc\":\"过氧乙酸：0.2%溶液用于手的消毒，浸泡2min；0.5%溶液用于餐具消毒，浸泡30～60min；1%～2%溶液用于室内空气消毒；1%溶液用于体温表消毒，浸泡30min。过氧乙酸对金属有腐蚀性，不能浸泡金属类物品，且应现配现用并放于阴凉处，以防高温引起爆炸。\"}');
INSERT INTO `mgy_exam_ques` VALUES (101, '{\"title\":\"25cm持物镊浸泡消毒时，容器内消毒液的高度是\",\"option\":{\"A\":\"9cm\",\"B\":\"13cm\",\"C\":\"15cm\",\"D\":\"17cm\",\"E\":\"19cm\"},\"answer\":\"B\",\"desc\":\"无菌持物钳浸泡在盛有消毒液的大口容器内，容器底部垫以无菌纱布，液面浸没轴节以上2～3cm或镊子的1/2。\"}');
INSERT INTO `mgy_exam_ques` VALUES (102, '{\"title\":\"对铜绿假单胞菌感染伤口换下的敷料，正确的处理方法为\",\"option\":{\"A\":\"清洗后再消毒\",\"B\":\"清洗后放日光下暴晒\",\"C\":\"灭菌后再清洗\",\"D\":\"扔入污物桶\",\"E\":\"焚烧\"},\"answer\":\"E\",\"desc\":\"焚烧法用途：①常用于无保留价值的物品，如污染的纸张、特殊感染（如破伤风、炭疸、绿脓杆菌）的敷料等；②搪瓷类和金属类（贵重器械、锐利刀剪除外）物品急用或无条件用其他方法消毒时也可用之。\"}');
INSERT INTO `mgy_exam_ques` VALUES (103, '{\"title\":\"高压蒸汽灭菌达到灭菌目的宜用\",\"option\":{\"A\":\"压力70kPa，温度116.2℃，时间10min\",\"B\":\"压力80kPa，温度118.2℃，时间15min\",\"C\":\"压力90kPa，温度120.2℃，时间20min\",\"D\":\"压力103～137kPa，温度121～126℃，时间20～30min\",\"E\":\"压力140～145kPa，温度125～130℃，时间25min\"},\"answer\":\"D\",\"desc\":\"压力蒸汽灭菌法是一种医院使用最普遍、效果最可靠的首选灭菌方法。利用高压下的高温饱和蒸汽杀灭所有微生物及其芽孢，高热释放的潜热可增强灭菌效果。优点是穿透力强，能达物品深部，灭菌效果可靠。当压力在103～137kPa，温度达121～126℃时，经20～30min即达灭菌目的。\"}');
INSERT INTO `mgy_exam_ques` VALUES (104, '{\"title\":\"使用紫外线照射消毒，关灯后再次使用前需间隔\",\"option\":{\"A\":\"1～2min\",\"B\":\"3～4min\",\"C\":\"5～6min\",\"D\":\"7～8min\",\"E\":\"9～10min\"},\"answer\":\"B\",\"desc\":\"常用紫外线灯的波长为254nm，这也是最佳杀菌波长。若需再次使用，关灯后需间歇3～4min后再开启。\"}');
INSERT INTO `mgy_exam_ques` VALUES (105, '{\"title\":\"穿脱隔离衣时要避免污染的部位是\",\"option\":{\"A\":\"腰带以下\",\"B\":\"腰带\",\"C\":\"领子\",\"D\":\"袖子后面\",\"E\":\"胸前、背后\"},\"answer\":\"C\",\"desc\":\"穿脱隔离衣时要避免污染的部位是领子。\"}');
INSERT INTO `mgy_exam_ques` VALUES (106, '{\"title\":\"下列隔离衣使用的要求，正确的是\",\"option\":{\"A\":\"每周更换一次\",\"B\":\"要保持袖口内外面清洁\",\"C\":\"必须完全盖住工作服\",\"D\":\"隔离衣潮湿，晾干后再使用\",\"E\":\"隔离衣挂在走廊内应外面向\"},\"answer\":\"C\",\"desc\":\"穿隔离衣时，隔离衣的长短要合适，须全部遮盖工作服；如有破洞，应补好后再穿。系领子时污染的袖口不可触及衣领、面部和帽子；后侧边缘须对齐，折叠处不能松散。穿好隔离衣后，双臂保持在腰部以上、视线范围内。不得进入清洁区，避免接触清洁物品。脱隔离衣时，先解开腰带，在前面打一活结；解开袖口，在肘部将部分衣袖塞入工作衣袖内。消毒双手，解开领扣，一手伸入另一侧袖口内，拉下衣袖过手（遮住手），再用衣袖遮住的手在外面拉下另一衣袖，两手在袖内使袖子对齐，双臂逐渐退出。注意：刷手时不能弄湿隔离衣，隔离衣也不能污染水池。注意保持衣领清洁。隔离衣挂在半污染区，清洁面向外；挂在污染区则污染面向外。隔离衣每日更换，如有潮湿或污染，应立即更换。\"}');
INSERT INTO `mgy_exam_ques` VALUES (107, '{\"title\":\"昏迷病人口腔护理禁忌\",\"option\":{\"A\":\"头侧向一侧\",\"B\":\"用张口器\",\"C\":\"漱口\",\"D\":\"用血管钳夹紧棉球擦拭\",\"E\":\"取下假牙浸泡清水中\"},\"answer\":\"C\",\"desc\":\"昏迷患者禁忌漱口。用开口器时，应从臼齿处放入。擦洗时血管钳须夹紧棉球，每次一个，防止棉球遗留在口腔内。\"}');
INSERT INTO `mgy_exam_ques` VALUES (108, '{\"title\":\"在身体空隙处垫以软枕的作用是\",\"option\":{\"A\":\"架空受压部位\",\"B\":\"降低局部皮肤所承受的压力\",\"C\":\"减少皮肤受摩擦的刺激\",\"D\":\"固定体位\",\"E\":\"安全防护，防止坠床\"},\"answer\":\"B\",\"desc\":\"在身体空隙处垫以软枕的作用是降低局部皮肤所承受的压力。\"}');
INSERT INTO `mgy_exam_ques` VALUES (109, '{\"title\":\"哪种患者需做特殊口腔护理\",\"option\":{\"A\":\"消化不良\",\"B\":\"胃炎\",\"C\":\"肺脓肿\",\"D\":\"支气管扩张\",\"E\":\"昏迷\"},\"answer\":\"E\",\"desc\":\"特殊口腔护理法适用于禁食、高热、昏迷、鼻饲、大手术后、口腔疾患及生活不能自理的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (110, '{\"title\":\"关于炎性浸润期压疮，下列哪项不正确\",\"option\":{\"A\":\"皮肤呈紫色\",\"B\":\"皮下硬结\",\"C\":\"有大、小水疱\",\"D\":\"水疱表皮剥脱，露出湿润的创面\",\"E\":\"创面上有脓性分泌物\"},\"answer\":\"E\",\"desc\":\"压疮的分期与临床表现：①淤血红润期，为压疮初期。局部皮肤受压，出现暂时血液循环障碍，表现为红肿、热、麻木或触痛。此期皮肤表面无破损情况，为可逆性改变。②炎性浸润期，红肿部位继续受压，血液循环得不到改善，静脉回流受阻，受压部位因淤血而呈现紫红色，有皮下硬节和（或）有水疱形成。水疱破溃后，可见潮湿红润的创面，病人有疼痛感。③溃疡期，静脉血回流严重受阻，局部淤血导致血栓形成，组织缺血、缺氧。轻者表皮水疱破溃后出现真皮层组织感染，浅层组织坏死，溃疡形成；重者坏死组织发黑，脓性分泌物增多，有臭味，可向深部扩散，甚至到达骨骼，更严重者还可出现脓毒败血症。\"}');
INSERT INTO `mgy_exam_ques` VALUES (111, '{\"title\":\"头虱可造成疾病的传播，如回归热、斑疹伤寒等。因此，发现病人有虱应立即彻底消灭。常用药液是百部酊，灭头虱用的百部酊药液成分是\",\"option\":{\"A\":\"百部100g+50%乙醇300ml+纯乙酸10ml\",\"B\":\"百部50g+50%乙醇100ml+纯乙酸10ml\",\"C\":\"百部30g+50%乙醇100ml+纯酸1ml\",\"D\":\"百部30g+50%乙醇100ml+纯酸10ml\",\"E\":\"百部30g+50%乙醇100ml+纯乙酸1ml\"},\"answer\":\"E\",\"desc\":\"常用灭虱药液：①30%含酸百部酊，百部30g加50%乙醇100ml，再加100%乙酸1ml，装入瓶中加盖盖严，48h后方可使用。②30%百部含酸煎剂，百部30g加水500ml煎煮30分钟，用双层纱布过滤，挤出药液；将药渣再加水500ml煎煮30分钟过滤，挤出药液；将两次药液合并再煎至100ml，待冷却后，加入乙酸即可。\"}');
INSERT INTO `mgy_exam_ques` VALUES (112, '{\"title\":\"关于头发护理，下列叙述错误的是\",\"option\":{\"A\":\"在病人的床上洗头时，病室的室温是24℃，水温是40～45℃\",\"B\":\"身体虚弱的病人，不易选择床上洗头\",\"C\":\"当病人的头发打结成团时，可以选择30%的乙醇\",\"D\":\"对于有头虱的病人，可以选择30%的含酸百部酊剂进行灭虮处理\",\"E\":\"含酸百部酊制剂包裹头发后，48h后取下\"},\"answer\":\"E\",\"desc\":\"应在24h后，取下帽子，用篦子篦去死虱和虮。\"}');
INSERT INTO `mgy_exam_ques` VALUES (113, '{\"title\":\"体位与褥疮好发部位的关系不正确的是\",\"option\":{\"A\":\"仰卧——骶尾部\",\"B\":\"侧卧——髋部\",\"C\":\"俯卧——内踝\",\"D\":\"坐位——坐骨结节\",\"E\":\"侧卧——肩峰部\"},\"answer\":\"C\",\"desc\":\"俯卧位时褥疮好发部位有额部、下颌、肩峰、髂前上棘、膝前部、足趾等，内踝是侧卧位时褥疮的好发部位。其余选项均正确。\"}');
INSERT INTO `mgy_exam_ques` VALUES (114, '{\"title\":\"李某，男，48岁，截瘫，尾骶部有一创面，面积2cm×15cm，深达肌层，有脓性分泌物，创面周围有黑色坏死组织。该创面应如何处理\",\"option\":{\"A\":\"用50%乙醇按摩创面及周围皮肤\",\"B\":\"用生理盐水冲洗并敷盖新鲜蛋膜\",\"C\":\"暴露创面、紫外线每日照射1次\",\"D\":\"剪去坏死组织，用双氧水冲洗，置引流纱条\",\"E\":\"涂厚层滑石粉包扎\"},\"answer\":\"D\",\"desc\":\"溃疡期护理要点：除全身和局部措施外，应根据伤口情况，按外科换药法处理。创面有感染时，局部处理原则是解除压迫，清洁创面，祛腐生新，促进愈合。该期亦可辅以红外线照射，使疮面干燥，有利于组织修复。\"}');
INSERT INTO `mgy_exam_ques` VALUES (115, '{\"title\":\"口腔绿脓杆菌感染时选用的含漱液是\",\"option\":{\"A\":\"1%～3%过氧化氢溶液\",\"B\":\"复方硼酸溶液\",\"C\":\"1%～4%碳酸氢钠溶液\",\"D\":\"0.02%呋喃西林溶液\",\"E\":\"0.1%醋酸溶液\"},\"answer\":\"E\",\"desc\":\"0.1%醋酸溶液用于铜绿假单胞菌感染，适用于口腔pH偏碱性时。\"}');
INSERT INTO `mgy_exam_ques` VALUES (116, '{\"title\":\"真菌感染时选用的含漱液是\",\"option\":{\"A\":\"1%～3%过氧化氢溶液\",\"B\":\"复方硼酸溶液\",\"C\":\"1%～4%碳酸氢钠溶液\",\"D\":\"0.02%呋喃西林溶液\",\"E\":\"0.1%醋酸溶液\"},\"answer\":\"C\",\"desc\":\"1%～4%碳酸氢钠溶液用于真菌感染，适用于口腔pH偏酸性时。\"}');
INSERT INTO `mgy_exam_ques` VALUES (117, '{\"title\":\"成人的脉搏每分钟低于60次时称缓脉，常见于\",\"option\":{\"A\":\"休克病人\",\"B\":\"虚脱病人\",\"C\":\"高血压病\",\"D\":\"颅内压增高病人\",\"E\":\"心房纤维性颤动病人\"},\"answer\":\"D\",\"desc\":\"缓脉：见于颅内压增高、房宣传导阻滞等患者。\"}');
INSERT INTO `mgy_exam_ques` VALUES (118, '{\"title\":\"缩窄性心包炎表现为\",\"option\":{\"A\":\"间歇脉\",\"B\":\"奇脉\",\"C\":\"丝脉\",\"D\":\"洪脉\",\"E\":\"水冲脉\"},\"answer\":\"B\",\"desc\":\"缩窄性心包炎表现为奇脉。\"}');
INSERT INTO `mgy_exam_ques` VALUES (119, '{\"title\":\"休克病人的脉搏是\",\"option\":{\"A\":\"缓脉\",\"B\":\"丝脉\",\"C\":\"绌脉\",\"D\":\"洪脉\",\"E\":\"不规则脉\"},\"answer\":\"B\",\"desc\":\"丝脉：当心输出量减少，动脉充盈度降低时，脉搏细弱无力，称丝脉。见于心功能不全、大出血、休克等患者。\"}');
INSERT INTO `mgy_exam_ques` VALUES (120, '{\"title\":\"动脉硬化病人可出现\",\"option\":{\"A\":\"绌脉\",\"B\":\"丝脉\",\"C\":\"洪脉\",\"D\":\"间歇脉\",\"E\":\"弦脉\"},\"answer\":\"E\",\"desc\":\"动脉硬化时管壁变硬失去弹性，呈迂曲状，诊脉时有紧张条索感，如按在琴弦上。常见于动脉硬化病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (121, '{\"title\":\"高热病人降温时首选\",\"option\":{\"A\":\"头部冷敷\",\"B\":\"腹股沟冷敷\",\"C\":\"乙醇拭浴\",\"D\":\"冷盐水灌肠\",\"E\":\"服退热药\"},\"answer\":\"A\",\"desc\":\"宜用物理降温；物理降温后半小时测量体温。\"}');
INSERT INTO `mgy_exam_ques` VALUES (122, '{\"title\":\"洋地黄中毒病人可出现\",\"option\":{\"A\":\"绌脉\",\"B\":\"丝脉\",\"C\":\"洪脉\",\"D\":\"间歇脉\",\"E\":\"弦脉\"},\"answer\":\"D\",\"desc\":\"间歇脉：在一系列正常均匀的脉搏中出现一次提前而较弱的搏动，其后有一较正常延长的间歇，称为间歇脉。二联律、三联律：每隔一个或两个正常搏动后出现一次过早搏动，则前者称二联律，后者称三联律。多见于心脏病患者或洋地黄中毒者。正常人在过度疲劳、精神兴奋、体位改变时偶尔也会出现间歇脉。\"}');
INSERT INTO `mgy_exam_ques` VALUES (123, '{\"title\":\"关于测量血压的注意事项，下列选项错误的是\",\"option\":{\"A\":\"打气不可过猛过高\",\"B\":\"血压未听清时，立即重新注气，再仔细听\",\"C\":\"须密切观察血压者，应尽量做到“四定”\",\"D\":\"偏瘫病人应在健侧上臂测\",\"E\":\"听诊器的胸端不可放在袖带下面\"},\"answer\":\"B\",\"desc\":\"测得的血压异常或听不清搏动时，需重新测量。重复测量血压，要驱净袖带内气体，使汞柱降至“0”点，稍待片刻再测，避免连续加压，使肢体循环受阻，影响测量数值。\"}');
INSERT INTO `mgy_exam_ques` VALUES (124, '{\"title\":\"发热的最常见原因是\",\"option\":{\"A\":\"恶性肿瘤\",\"B\":\"物理因子作用\",\"C\":\"化学因子作用\",\"D\":\"细菌、病毒感染\",\"E\":\"真菌感染\"},\"answer\":\"D\",\"desc\":\"引起发热的最常见原因是细菌、病毒感染。\"}');
INSERT INTO `mgy_exam_ques` VALUES (125, '{\"title\":\"败血症病人发热常见的热型是\",\"option\":{\"A\":\"间歇热\",\"B\":\"弛张热\",\"C\":\"不规则热\",\"D\":\"波浪热\",\"E\":\"回归热\"},\"answer\":\"B\",\"desc\":\"弛张热：体温在39.0℃以上，体温高低不一，日差大于1℃，最低温度高于正常水平。见于败血症等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (126, '{\"title\":\"房颤病人的脉搏是\",\"option\":{\"A\":\"间歇脉\",\"B\":\"缓脉\",\"C\":\"三联律\",\"D\":\"绌脉\",\"E\":\"速脉\"},\"answer\":\"D\",\"desc\":\"脉搏短绌：在单位时间内脉率少于心率，快慢不一，强弱不等，极不规则。见于心房颤动的患者。\"}');
INSERT INTO `mgy_exam_ques` VALUES (127, '{\"title\":\"胆囊造影检查前日中午病人进脂肪饮食的目的是\",\"option\":{\"A\":\"刺激胆囊产生胆囊收缩素\",\"B\":\"刺激肝细胞多分泌胆汁\",\"C\":\"有利于胆汁进入胆囊\",\"D\":\"刺激胆囊收缩和排空\",\"E\":\"有助于造影剂进入肝细胞\"},\"answer\":\"D\",\"desc\":\"造影前1天午餐进高脂肪饮食，刺激胆囊收缩排空，有助于造影剂进入胆囊。\"}');
INSERT INTO `mgy_exam_ques` VALUES (128, '{\"title\":\"出入液量记录方法中错误的是\",\"option\":{\"A\":\"早7时至晚7时用蓝笔\",\"B\":\"晚7时至次日晨7时用红笔\",\"C\":\"白班总结24h出入量\",\"D\":\"夜班总结24h出入量\",\"E\":\"用蓝笔填写在体温单的相应专栏内\"},\"answer\":\"C\",\"desc\":\"记录出入液量时，夜班应总结24h出入量。\"}');
INSERT INTO `mgy_exam_ques` VALUES (129, '{\"title\":\"禁用高蛋白饮食的病人是\",\"option\":{\"A\":\"严重贫血病人\",\"B\":\"肾病综合征病人\",\"C\":\"肝昏迷病人\",\"D\":\"大手术后病人\",\"E\":\"肺结核病人\"},\"answer\":\"C\",\"desc\":\"低蛋白饮食适用范围：用于限制蛋白质摄入的病人，如急性肾炎、尿毒症、肝昏迷等；饮食原则：限制蛋白质摄入，成人每日蛋白质总量不超过40g，应多补充蔬菜和含糖量较高的食物。\"}');
INSERT INTO `mgy_exam_ques` VALUES (130, '{\"title\":\"哪种不是要素饮食的成分\",\"option\":{\"A\":\"麦芽糖\",\"B\":\"必需氨基酸\",\"C\":\"非必需氨基酸\",\"D\":\"维生素\",\"E\":\"乳剂\"},\"answer\":\"A\",\"desc\":\"要素饮食又称为要素膳、化学膳、元素膳，它是由人工配制的、含有全部人体生理需要的各种营养成分，不需要消化或进行很少消化即可吸收的无渣饮食。\"}');
INSERT INTO `mgy_exam_ques` VALUES (131, '{\"title\":\"给危重病人喂食时哪项不妥\",\"option\":{\"A\":\"宜小口喂食，以便咀嚼和吞咽\",\"B\":\"昏迷者可采用鼻饲法\",\"C\":\"进流质者可用吸管或水壶吸吮\",\"D\":\"应先喂固态食物，后喂液态食物\",\"E\":\"卧床者应使其头转向一侧\"},\"answer\":\"D\",\"desc\":\"给危重病人喂食时应先喂液态食物，后喂固态食物。\"}');
INSERT INTO `mgy_exam_ques` VALUES (132, '{\"title\":\"高蛋白饮食不适用于\",\"option\":{\"A\":\"结核病\",\"B\":\"肾病综合征\",\"C\":\"急性肾炎\",\"D\":\"贫血\",\"E\":\"大手术后\"},\"answer\":\"C\",\"desc\":\"低蛋白饮食适用范围：用于限制蛋白质摄入的病人，如急性肾炎、尿毒症、肝昏迷等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (133, '{\"title\":\"疾病恢复期病人给\",\"option\":{\"A\":\"普通饮食\",\"B\":\"软质饮食\",\"C\":\"半流质饮食\",\"D\":\"流质饮食\",\"E\":\"禁食\"},\"answer\":\"A\",\"desc\":\"普通饮食适用范围：病情较轻、疾病恢复期，无发热、无消化道疾患以及不需要限制饮食的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (134, '{\"title\":\"关于要素饮食的描述不妥的是\",\"option\":{\"A\":\"是天然合成的营养饮食\",\"B\":\"不需消化液也能吸收\",\"C\":\"符合机体生理需要\",\"D\":\"适用于胃肠道瘘、急性胰腺炎等病人\",\"E\":\"可口服、鼻饲或造痿管滴入\"},\"answer\":\"A\",\"desc\":\"要素饮食为要素膳、化学膳、元素膳，它是由人工配制的、含有全部人体生理需要的各种营养成分，不需要消化或进行很少消化即可吸收的无渣饮食。\"}');
INSERT INTO `mgy_exam_ques` VALUES (135, '{\"title\":\"禁忌使用鼻饲法的病人是\",\"option\":{\"A\":\"昏迷病人\",\"B\":\"口腔手术病人\",\"C\":\"食管狭窄病人\",\"D\":\"拒绝进食病人\",\"E\":\"食管下段静脉曲张病人\"},\"answer\":\"E\",\"desc\":\"凡上消化道出血、食管静脉曲张或梗阻以及鼻腔手术后的病人禁用鼻饲法。\"}');
INSERT INTO `mgy_exam_ques` VALUES (136, '{\"title\":\"胆囊造影时采用的试验饮食是\",\"option\":{\"A\":\"高热量饮食\",\"B\":\"高脂肪饮食\",\"C\":\"低蛋白饮食\",\"D\":\"低脂肪饮食\",\"E\":\"低盐饮食\"},\"answer\":\"B\",\"desc\":\"胆囊造影饮食造影前一天午餐进高脂肪饮食，刺激胆囊收缩排空，有助于造影剂进入胆囊。\"}');
INSERT INTO `mgy_exam_ques` VALUES (137, '{\"title\":\"给昏迷病人插胃管时，将下颌贴近胸骨柄，是为了\",\"option\":{\"A\":\"增大咽喉部通道弧度\",\"B\":\"顺利通过气管分叉处\",\"C\":\"顺利通过膈肌\",\"D\":\"减少食道黏膜损伤\",\"E\":\"减轻病人不适\"},\"answer\":\"A\",\"desc\":\"迷病人，由于吞咽反射和咳嗽反射消失，不能合作，为提高插管的成功率，在插管前应注意：①插管前，去枕，头后仰；②胃管插至14～16cm时，用左手将病人头部托起，使其下颌靠近胸骨柄，以增大咽喉部通道的弧度，便于将胃管插入预定的长度。\"}');
INSERT INTO `mgy_exam_ques` VALUES (138, '{\"title\":\"记录每日排出量不包括\",\"option\":{\"A\":\"每日尿量\",\"B\":\"汗液排出量\",\"C\":\"粪便量\",\"D\":\"胃肠减压抽出量\",\"E\":\"胸、腹腔抽出液及胆汁引流量\"},\"answer\":\"B\",\"desc\":\"每日排出量包括尿量，粪便量以及其他排出液如胃肠减压抽出液，胸腹腔抽出液，痰、呕吐液、伤口渗出液、胆汁引流液等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (139, '{\"title\":\"滴入要素饮食的温度和滴速搭配正确的是\",\"option\":{\"A\":\"温度40～45℃，滴速80～90gtt/min\",\"B\":\"温度39～43℃，滴速70～90gtt/min\",\"C\":\"温度39～42℃，滴速60～80gtt/min\",\"D\":\"温度38～41℃，滴速50～70gtt/min\",\"E\":\"温度38～40℃，滴速40～60gtt/min\"},\"answer\":\"E\",\"desc\":\"要素饮食饮食原则：可口服、鼻饲或造瘘置管滴注，温度保持在38～40℃，滴速40～60gtt/min，最快不宜超过150ml/h。\"}');
INSERT INTO `mgy_exam_ques` VALUES (140, '{\"title\":\"在鼻饲插管过程中病人出现呛咳、发绀，应采取的措施是\",\"option\":{\"A\":\"嘱病人深呼吸\",\"B\":\"嘱病人做吞咽动作\",\"C\":\"托起病人的头部插管\",\"D\":\"用注射器抽吸胃液\",\"E\":\"拔出胃管休息片刻后再插管\"},\"answer\":\"E\",\"desc\":\"如病人出现呛咳、呼吸困难、发绀等情况，表示误入气管，应立即拔出，嘱病人休息片刻重插。\"}');
INSERT INTO `mgy_exam_ques` VALUES (141, '{\"title\":\"张女士，因心脏病导致右侧心力衰竭，尿少，右侧腹部胀痛，下肢水肿，护士指导病人饮食，每日限制食盐量不超过\",\"option\":{\"A\":\"3g\",\"B\":\"2g\",\"C\":\"4g\",\"D\":\"0.8g\",\"E\":\"0.7g\"},\"answer\":\"B\",\"desc\":\"低盐饮食适用范围：用于急慢性肾炎、心脏病、肝硬化腹水、重度高血压但水肿较轻病人；饮食原则：限制食盐的摄入，成人摄入食盐每日可用不超过2g（含钠0.8g），但不包括食物内自然存在的氯化钠，禁止一切腌制食物，如咸菜、咸肉、香肠、火腿、皮蛋。\"}');
INSERT INTO `mgy_exam_ques` VALUES (142, '{\"title\":\"下列哪种情况禁用热疗法\",\"option\":{\"A\":\"循环不良\",\"B\":\"感觉迟钝\",\"C\":\"各种脏器内出血\",\"D\":\"炎症晚期\",\"E\":\"四肢厥冷\"},\"answer\":\"C\",\"desc\":\"热疗的禁忌证：①急腹症尚未明确诊断前。热疗能够减轻疼痛，因而掩盖病情真相而贻误诊断和治疗。②面部危险三角区感染化脓时。因面部危险三角区血管丰富又无静脉瓣，且与颅内海绵窦相通，热疗能使该处血管扩张，血流量增多，导致细菌和毒素进入血液循环，使炎症扩散，造成颅内感染和败血症。③各种脏器内出血时。因热疗可使局部血管扩张，增加脏器的血流量和血管的通透性，而加重出血倾向。④软组织损伤早期（48h）。软组织损伤，如挫伤、扭伤或砸伤等早期，忌用热疗。因热疗可促进局部血循环，从而加重皮下出血、肿胀及疼痛。\"}');
INSERT INTO `mgy_exam_ques` VALUES (143, '{\"title\":\"热疗可减轻深部组织充血的机理是\",\"option\":{\"A\":\"增加新陈代谢和白细胞吞噬功能\",\"B\":\"能降低痛觉神经的兴奋性，改善血液循环\",\"C\":\"刺激神经末梢引起反射作用，使局部血管扩张\",\"D\":\"抑制细胞活动，使神经末梢的敏感性降低\",\"E\":\"降低细菌的活力和细胞的代谢\"},\"answer\":\"C\",\"desc\":\"热疗可使局部血管扩张，体表血流增加，因而相对减轻深部组织的充血。\"}');
INSERT INTO `mgy_exam_ques` VALUES (144, '{\"title\":\"冷疗可制止炎症扩散和化脓的机制是\",\"option\":{\"A\":\"增加新陈代谢和白细胞吞噬功能\",\"B\":\"能降低痛觉神经的兴奋性，改善血液循环\",\"C\":\"刺激神经末梢引起反射作用，使局部血管扩张\",\"D\":\"抑制细胞活动，使神经末梢的敏感性降低\",\"E\":\"降低细菌的活力和细胞的代谢\"},\"answer\":\"E\",\"desc\":\"冷疗可使皮肤血管收缩，局部血流减少、减慢，降低细胞新陈代谢和微生物的活力，限制了炎症的扩散，常用于炎症早期的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (145, '{\"title\":\"局部热水浸泡的适宜水温为\",\"option\":{\"A\":\"30～35℃\",\"B\":\"36～42℃\",\"C\":\"40～45℃\",\"D\":\"47～50℃\",\"E\":\"50～55℃\"},\"answer\":\"C\",\"desc\":\"局部浸泡调节水温至40～45℃。\"}');
INSERT INTO `mgy_exam_ques` VALUES (146, '{\"title\":\"下列关于热疗能促进炎症消散或局限的说法中错误的是\",\"option\":{\"A\":\"使局部血管扩张充血，改善血液循环\",\"B\":\"增强新陈代谢和白细胞吞噬功能\",\"C\":\"促进炎性渗出物的吸收和消散\",\"D\":\"溶解坏死组织，有助于组织修复\",\"E\":\"降低细菌活力和细胞代谢\"},\"answer\":\"E\",\"desc\":\"热疗可使局部血管扩张，血流速度加快，利于组织中毒素的排出；同时促进血液循环，增加血流量，加快新陈代谢，增强白细胞的吞噬功能。因此，在炎症早期用热，可促进炎性渗出物的吸收和消散；在炎症后期用热，可因白细胞释放蛋白溶解酶，溶解坏死组织，从而有助于坏死组织的清除及组织修复，使炎症局限。\"}');
INSERT INTO `mgy_exam_ques` VALUES (147, '{\"title\":\"应用热疗的目的是\",\"option\":{\"A\":\"促进浅表炎症消散和局限\",\"B\":\"抑制炎症扩散\",\"C\":\"减轻局部充血或出血\",\"D\":\"传导发散体内的热\",\"E\":\"提高痛觉神经的兴奋性\"},\"answer\":\"A\",\"desc\":\"热疗的目的：①促进炎症的消散和局限。热疗可使局部血管扩张，血流速度加快，利于组织中毒素的排出；同时促进血液循环，增加血流量，加快新陈代谢，增强白细胞的吞噬功能。因此，在炎症早期用热，可促进炎性渗出物的吸收和消散；在炎症后期用热，可因白细胞释放蛋白溶解酶，溶解坏死组织，从而有助于坏死组织的清除及组织修复，使炎症局限。②缓解疼痛。热疗能降低痛觉神经的兴奋性，改善血液循环，减轻炎性水肿，加速致痛物质的排出及渗出物的吸收，从而解除局部神经末梢的压力。热疗还可使肌肉、肌腱和韧带等组织松弛，可缓解因肌肉痉挛、关节强直而引起的疼痛。常用于腰肌劳损、肾绞痛、胃肠痉挛等病人。③减轻深部组织充血。热疗可使局部血管扩张，体表血流增加，因而相对减轻深部组织的充血。④保暧。热疗可使局部血管扩张，促进血液循环，使病人感到温暖舒适。多用于危重、年老体弱、小儿及末梢循环不良病人的保暖。\"}');
INSERT INTO `mgy_exam_ques` VALUES (148, '{\"title\":\"为达到保暖、解痉、镇痛的目的，选用的热疗法是\",\"option\":{\"A\":\"湿热敷\",\"B\":\"温水浴\",\"C\":\"热水坐浴\",\"D\":\"热水袋\",\"E\":\"红外线照射\"},\"answer\":\"D\",\"desc\":\"热水袋的使用目的：常用于保暖、解痉、镇痛。\"}');
INSERT INTO `mgy_exam_ques` VALUES (149, '{\"title\":\"酒精擦浴时，禁擦的部位是\",\"option\":{\"A\":\"侧颈、上肢\",\"B\":\"腋窝、腹股沟\",\"C\":\"前胸、腹部\",\"D\":\"臀部、下肢\",\"E\":\"手掌、脚心\"},\"answer\":\"C\",\"desc\":\"禁忌用冷的部位：①枕后、耳郭、阴囊（防止冻伤）。用冷易引起冻伤。②心前区。用冷可引起反射性心率减慢、心律失常。③腹部。用冷易引起腹泻。④足底。用冷可引起反射性末梢血管收缩，影响散热；还可以引起一过性冠状动脉收缩。\"}');
INSERT INTO `mgy_exam_ques` VALUES (150, '{\"title\":\"扁桃体手术后预防出血的最好方法是\",\"option\":{\"A\":\"病人取半坐位\",\"B\":\"颈部用冰囊\",\"C\":\"应用止血药\",\"D\":\"嘱病人喝温开水\",\"E\":\"头部置冰槽\"},\"answer\":\"B\",\"desc\":\"局部冷疗可使皮肤血管收缩，局部血流量减少，血流速减慢，从而减轻局部组织的充血、出血。常用于扁桃体摘除术后、鼻出血、软组织损伤早期的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (151, '{\"title\":\"某护士不慎将开水溅在足背上，局部灼痛感，皮肤潮红、无水疱。立即用冷毛巾行局部冷敷。其主要作用是\",\"option\":{\"A\":\"降低神经末梢兴奋性，减轻疼痛\",\"B\":\"使局部血管收缩，减少渗出\",\"C\":\"通过传导使局部散热\",\"D\":\"防止感染\",\"E\":\"增加局部代谢，加速愈合过程\"},\"answer\":\"B\",\"desc\":\"冷疗可使皮肤血管收缩，局部血流减少、减慢，降低细胞新陈代谢和微生物的活力，限制了炎症的扩散，常用于炎症早期的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (152, '{\"title\":\"保留灌肠，注入药量和溶液温度分别是\",\"option\":{\"A\":\"<200m1，39～41℃\",\"B\":\"<300m1，38～39℃\",\"C\":\"<400m1，37～38℃\",\"D\":\"<500m1，36～37℃\",\"E\":\"<600ml，35～36℃\"},\"answer\":\"A\",\"desc\":\"保留灌肠常用溶液液量小于200ml，温度39～41℃。\"}');
INSERT INTO `mgy_exam_ques` VALUES (153, '{\"title\":\"为解除盆腔术后肠胀气，应采用\",\"option\":{\"A\":\"保留灌肠\",\"B\":\"小量不保留灌肠\",\"C\":\"大量不保留灌肠\",\"D\":\"清洁灌肠\",\"E\":\"以上都不是\"},\"answer\":\"B\",\"desc\":\"小量不保留灌肠为腹盆腔术后、危重、老幼病人解除便秘，排除肠道积气，减轻腹胀。\"}');
INSERT INTO `mgy_exam_ques` VALUES (154, '{\"title\":\"清洁灌肠肛管插入长度为\",\"option\":{\"A\":\"7～10cm\",\"B\":\"14cm\",\"C\":\"20cm\",\"D\":\"23cm\",\"E\":\"26cm\"},\"answer\":\"C\",\"desc\":\"清洁灌肠肛管插入长度为20cm。\"}');
INSERT INTO `mgy_exam_ques` VALUES (155, '{\"title\":\"正常新鲜尿在一般情况下呈\",\"option\":{\"A\":\"碱性\",\"B\":\"弱酸性\",\"C\":\"弱碱性\",\"D\":\"中性\",\"E\":\"强酸性\"},\"answer\":\"B\",\"desc\":\"正常新鲜尿pH为4.5～7.5，平均值为6；呈弱酸性，正常晨尿因尿素分解产生氨，故有氨臭味。\"}');
INSERT INTO `mgy_exam_ques` VALUES (156, '{\"title\":\"男患者导尿管的正确固定方法是\",\"option\":{\"A\":\"环形\",\"B\":\"蝶形\",\"C\":\"全封闭式（缠绕）\",\"D\":\"十字交叉\",\"E\":\"蝶形+环形\"},\"answer\":\"E\",\"desc\":\"男患者导尿管的正确固定方法是蝶形+环形。\"}');
INSERT INTO `mgy_exam_ques` VALUES (157, '{\"title\":\"长期鼻饲病人的胃管更换应\",\"option\":{\"A\":\"隔日一次\",\"B\":\"每日一次\",\"C\":\"每日2次\",\"D\":\"每周一次\",\"E\":\"每周2次\"},\"answer\":\"D\",\"desc\":\"长期鼻饲病人的胃管更换应每周一次。\"}');
INSERT INTO `mgy_exam_ques` VALUES (158, '{\"title\":\"胆红素尿的颜色是\",\"option\":{\"A\":\"脓白色\",\"B\":\"黄褐色\",\"C\":\"红棕色\",\"D\":\"乳白色\",\"E\":\"淡黄色\"},\"answer\":\"B\",\"desc\":\"红色或棕色为肉眼血尿；酱油色或浓茶色为血红蛋白尿；黄褐色为胆红素尿；白色浑浊为脓尿；乳白色为乳糜尿。\"}');
INSERT INTO `mgy_exam_ques` VALUES (159, '{\"title\":\"呕吐呈喷射状，应考虑\",\"option\":{\"A\":\"高位性肠梗阻\",\"B\":\"颅内压增高\",\"C\":\"幽门梗阻\",\"D\":\"食物中毒\",\"E\":\"低位性肠梗阻\"},\"answer\":\"B\",\"desc\":\"颅内压增高典型的呕吐为喷射状。\"}');
INSERT INTO `mgy_exam_ques` VALUES (160, '{\"title\":\"肝昏迷病人灌肠时禁用肥皂水，是因为\",\"option\":{\"A\":\"肥皂水易引起腹胀\",\"B\":\"肥皂水易造成肠穿孔\",\"C\":\"可以减少氨的产生和吸收\",\"D\":\"可以防止发生水肿\",\"E\":\"可以防止发生酸中毒\"},\"answer\":\"C\",\"desc\":\"肝昏迷病人禁用肥皂水灌肠，以减少氨的产生和吸收。\"}');
INSERT INTO `mgy_exam_ques` VALUES (161, '{\"title\":\"下列情况可实施大量不保留灌肠的病人是\",\"option\":{\"A\":\"高热病人降温\",\"B\":\"心肌梗死病人\",\"C\":\"急腹症病人\",\"D\":\"消化道出血病人\",\"E\":\"妊娠早期病人\"},\"answer\":\"A\",\"desc\":\"大量不保留灌肠的目的：软化粪便，解除便秘及肠胀气；清洁肠道，为某些手术、检查或分娩做准备；减轻中毒；为高热病人降温。\"}');
INSERT INTO `mgy_exam_ques` VALUES (162, '{\"title\":\"留置导尿管的目的与下列哪项无关\",\"option\":{\"A\":\"便于留取中段尿标本做培养\",\"B\":\"为休克病人准确记录尿量\",\"C\":\"盆腔器官手术前准备\",\"D\":\"减轻膀胱手术后切口的张力\",\"E\":\"保持昏迷病人会阴部清洁干燥\"},\"answer\":\"A\",\"desc\":\"留置导尿管目的：抢救休克、危重病人时正确记录尿量，以观察病情；盆腔器官手术前引流尿液，避免术中误伤膀胱；某些泌尿系疾病手术后留置导尿管，便于引流和冲洗，并减轻手术切口的张力，有利于伤口的愈合；昏迷、瘫痪或会阴部有伤口者保留导尿管以保持会阴部清洁干燥。\"}');
INSERT INTO `mgy_exam_ques` VALUES (163, '{\"title\":\"青霉素试验时哪种方法可靠而且普遍采用\",\"option\":{\"A\":\"黏膜试验\",\"B\":\"快速试验\",\"C\":\"划痕试验\",\"D\":\"皮内试验\",\"E\":\"口含试验\"},\"answer\":\"D\",\"desc\":\"青霉素试验时采用皮内注射。\"}');
INSERT INTO `mgy_exam_ques` VALUES (164, '{\"title\":\"细胞色素C划痕试验的皮试液含量为\",\"option\":{\"A\":\"每毫升含0.75mg\",\"B\":\"每毫升含7.5mg\",\"C\":\"每毫升含2500U\",\"D\":\"每毫升含150U\",\"E\":\"每毫升含10000U\"},\"answer\":\"B\",\"desc\":\"细胞色素C划痕试验的含量为每毫升含7.5mg。\"}');
INSERT INTO `mgy_exam_ques` VALUES (165, '{\"title\":\"宜饭前服用的药物是\",\"option\":{\"A\":\"胃蛋白酶合剂\",\"B\":\"维生素C\",\"C\":\"溴化铵\",\"D\":\"氨茶碱\",\"E\":\"颠茄合剂\"},\"answer\":\"A\",\"desc\":\"按药物性能，掌握服用中的注意事项。对牙齿有腐蚀作用和使牙齿染色的药物，如酸类、铁剂，服用时可用饮水管吸入，服药后漱口；服用铁剂禁忌饮茶，以免铁盐形成，妨碍药物的吸收。刺激食欲的健胃药应饭前服，以增进食欲。助消化药及对胃黏膜有刺激性的药物应饭后服，有利于食物消化或减少对胃壁的刺激。止咳糖浆服后不宜饮水，以免降低疗效。同时服用多种药物时，应最后服用止咳糖浆。磺胺类药服后多饮水，防止尿少时引起肾小管阻塞。服退热药时多饮水可增强药物疗效。服强心苷类药物应先测量心率及节律，如脉率低于60次/分或节律异常应停服。\"}');
INSERT INTO `mgy_exam_ques` VALUES (166, '{\"title\":\"应存放在有色密盖瓶内的一类药物是\",\"option\":{\"A\":\"易氧化\",\"B\":\"易挥发\",\"C\":\"易潮解\",\"D\":\"易燃烧\",\"E\":\"易风化\"},\"answer\":\"A\",\"desc\":\"易氧化和遇光变质的药物，装在有色密盖瓶中放阴凉处，如维生素、氨茶碱等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (167, '{\"title\":\"有关口服药的规定，哪项是错误的\",\"option\":{\"A\":\"鼻饲患者要停止用药\",\"B\":\"有疑问者，要重新核对\",\"C\":\"服药后，要观察反应\",\"D\":\"服安眠药者，防成瘾\",\"E\":\"服洋地黄药物，防中毒\"},\"answer\":\"A\",\"desc\":\"鼻饲患者可从胃管内注入药物。\"}');
INSERT INTO `mgy_exam_ques` VALUES (168, '{\"title\":\"有关皮内注射的论述，哪项是错误的\",\"option\":{\"A\":\"部位是前臂掌侧\",\"B\":\"消毒忌用碘酊\",\"C\":\"针尖与皮肤呈5°角\",\"D\":\"拔针时勿按压\",\"E\":\"只用于药物过敏试验\"},\"answer\":\"E\",\"desc\":\"皮内注射法可用于皮肤试验；预防接种；局麻先驱步骤。\"}');
INSERT INTO `mgy_exam_ques` VALUES (169, '{\"title\":\"下列皮下注射的做法错误的是\",\"option\":{\"A\":\"药液量少于1ml，须用1ml注射器抽吸\",\"B\":\"注射部位要常规消毒\",\"C\":\"持针时，右手食指固定针栓\",\"D\":\"针头和皮肤呈10°角刺入\",\"E\":\"抽吸无回血即可推药\"},\"answer\":\"D\",\"desc\":\"针尖与皮肤呈30°～40°角。\"}');
INSERT INTO `mgy_exam_ques` VALUES (170, '{\"title\":\"某慢性充血性心力衰竭患者在治疗期间出现恶心、头晕，黄视，心率为36次/min，应考虑\",\"option\":{\"A\":\"多巴酚丁胺中毒\",\"B\":\"酚妥拉明中毒\",\"C\":\"洋地黄中毒\",\"D\":\"硝普钠中毒\",\"E\":\"氨茶碱中毒\"},\"answer\":\"C\",\"desc\":\"慢性充血性心力衰竭患者应考虑洋地黄中毒。\"}');
INSERT INTO `mgy_exam_ques` VALUES (171, '{\"title\":\"急救链霉素过敏性休克时注射哪种钙剂最好\",\"option\":{\"A\":\"乳酸钙\",\"B\":\"葡萄糖酸钙\",\"C\":\"氯化钙\",\"D\":\"碳酸钙\",\"E\":\"溴化钙\"},\"answer\":\"C\",\"desc\":\"因链霉素可与钙离子络合，从而使链霉素的毒性症状减轻或消失。\"}');
INSERT INTO `mgy_exam_ques` VALUES (172, '{\"title\":\"血清制品为\",\"option\":{\"A\":\"青霉素\",\"B\":\"链霉素\",\"C\":\"破伤风抗毒素\",\"D\":\"普鲁卡因\",\"E\":\"细胞色素\"},\"answer\":\"C\",\"desc\":\"破伤风抗毒素为血清制品。\"}');
INSERT INTO `mgy_exam_ques` VALUES (173, '{\"title\":\"青霉素过敏性休克的呼吸道阻塞症状为\",\"option\":{\"A\":\"瘙痒、荨麻疹及其他皮疹\",\"B\":\"胸闷、气促，伴濒危感\",\"C\":\"头晕眼花、意识丧失、抽搐，大小便失禁\",\"D\":\"发热、关节肿痛、全身淋巴结肿大、腹痛\",\"E\":\"面色苍白、冷汗、发绀、脉细弱、血压下降\"},\"answer\":\"B\",\"desc\":\"青霉素过敏性休克：可发生在皮内试验后，首次用药及连续用药过程中，数秒钟或数分钟内闪电式发生。临床表现：①呼吸道阻塞症状：由于喉头水肿和肺水肿所致，表现胸闷、气急伴濒危感。②循环衰竭症状：表现为面色苍白、冷汗、发绀、脉细弱、血压下降。③中枢神经系统症状：可能由于脑组织缺氧所致，表现为头晕、眼花、面部及四肢麻木、烦躁不安、意识丧失、抽搐、大小便失禁等。④皮肤过敏症状：有瘙痒、荨麻疹等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (174, '{\"title\":\"常用外文缩写“每晚一次”是\",\"option\":{\"A\":\"qd\",\"B\":\"qh\",\"C\":\"qn\",\"D\":\"q6h\",\"E\":\"qid\"},\"answer\":\"C\",\"desc\":\"qd：每日1次，qn：每晚1次，qh：每小时1次，qid:每日4次，qod:隔日1次，tid:每日3次，bid:每日2次，biw:每周2次。\"}');
INSERT INTO `mgy_exam_ques` VALUES (175, '{\"title\":\"符合超声雾化吸入特点的是\",\"option\":{\"A\":\"雾量恒定，方便使用\",\"B\":\"雾滴颗粒大小不一\",\"C\":\"雾滴随呼吸可以达到肺泡\",\"D\":\"雾滴的直径可以在5mm以上\",\"E\":\"产生气雾温度低，治疗后不易着凉\"},\"answer\":\"C\",\"desc\":\"超声雾化吸入应用超声波声能，使药液变成细微的气雾，由呼吸道吸入。\"}');
INSERT INTO `mgy_exam_ques` VALUES (176, '{\"title\":\"臀大肌注射时，病人的最佳体位是\",\"option\":{\"A\":\"侧卧位，下腿伸直，上腿弯曲\",\"B\":\"侧卧位，上腿伸直，下腿弯曲\",\"C\":\"侧卧位，两腿伸直\",\"D\":\"平卧位，两腿弯曲\",\"E\":\"俯卧位，两脚跟相对\"},\"answer\":\"B\",\"desc\":\"臀大肌注射体位：①侧卧位：上腿伸直，下腿稍弯曲；②俯卧位：足尖相对，足跟分开；③仰卧位：用于危重者；④坐位：便于操作。\"}');
INSERT INTO `mgy_exam_ques` VALUES (177, '{\"title\":\"为两岁以下婴幼儿肌内注射，不恰当的是\",\"option\":{\"A\":\"宜选肌肉肥厚的臀大肌\",\"B\":\"注射时应固定好肢体，以防折针\",\"C\":\"切勿把针梗全部刺入\",\"D\":\"注意更换注射部位\",\"E\":\"注意药物的配伍禁忌\"},\"answer\":\"A\",\"desc\":\"2岁以下婴幼儿因臀大肌发育不完善，不宜选用臀大肌注射，应选臀中、小肌注射，以防损伤坐骨神经。\"}');
INSERT INTO `mgy_exam_ques` VALUES (178, '{\"title\":\"抢救青霉素过敏性休克，最关键的措施是\",\"option\":{\"A\":\"立即通知医生\",\"B\":\"立即停药，平卧、保暖\",\"C\":\"针刺人中、内关等穴位\",\"D\":\"立即皮下注射盐酸肾上腺素0.5～1mg\",\"E\":\"立即注射新斯的明0.1～1ml\"},\"answer\":\"D\",\"desc\":\"青霉素过敏性休克的处理：①应立即停药，就地抢救。病人平卧，以利脑部血液供应，并注意保暖。②按医嘱立即皮下注射0.1%肾上腺素0.5～1ml。此药是抢救过敏性休克的首选药，它具有收缩血管、增加外周阻力、兴奋心肌、增加心输出量及松弛支气管平滑肌的作用。\"}');
INSERT INTO `mgy_exam_ques` VALUES (179, '{\"title\":\"破伤风抗毒素过敏试验液0.1ml含破伤风抗毒素\",\"option\":{\"A\":\"5IU\",\"B\":\"15IU\",\"C\":\"150IU\",\"D\":\"1500IU\",\"E\":\"100IU\"},\"answer\":\"B\",\"desc\":\"破伤风抗毒素过敏试验，皮试液的配制成每毫升含150IU的TAT生理盐水溶液为标准，皮内注射15IU/0.1ml。\"}');
INSERT INTO `mgy_exam_ques` VALUES (180, '{\"title\":\"颈外静脉穿刺的正确操作方法是\",\"option\":{\"A\":\"持穿刺针呈45°进针，入皮后呈25°穿刺\",\"B\":\"持穿刺针呈25°进针，入皮后呈45°穿刺\",\"C\":\"持穿刺针呈60°进针，入皮后呈15°穿刺\",\"D\":\"持穿刺针呈15°进针，入皮后呈60°穿刺\",\"E\":\"持穿刺针呈30°进针，入皮后呈30°穿刺\"},\"answer\":\"A\",\"desc\":\"颈外静脉是颈部最大的浅静脉，行径表浅，易于穿刺与固定。持穿刺针进针角度与皮肤呈45°角，进入皮下改为25°角，再沿颈外静脉走向刺入。\"}');
INSERT INTO `mgy_exam_ques` VALUES (181, '{\"title\":\"发生溶血反应时，护士首先应\",\"option\":{\"A\":\"停止输血，保留余血\",\"B\":\"通知医生和家属，安慰病人\",\"C\":\"碱化尿液\",\"D\":\"密切观察生命体征和尿量\",\"E\":\"热敷双侧腰部\"},\"answer\":\"A\",\"desc\":\"立即停止输血，与医生联系，并保留余血。采集病人血标本，重做血型鉴定和交叉配血试验。\"}');
INSERT INTO `mgy_exam_ques` VALUES (182, '{\"title\":\"小儿头皮静脉输液时，如误注入动脉，局部表现为\",\"option\":{\"A\":\"局部无变化\",\"B\":\"苍白、水肿\",\"C\":\"呈树枝状分布苍白\",\"D\":\"沿静脉走向呈条索状红线\",\"E\":\"局部发绀、水肿\"},\"answer\":\"C\",\"desc\":\"小儿头皮静脉输液时，如误注入动脉，局部表现为呈树枝状分布苍白。\"}');
INSERT INTO `mgy_exam_ques` VALUES (183, '{\"title\":\"补充电解质用\",\"option\":{\"A\":\"10%葡萄糖溶液\",\"B\":\"林格溶液\",\"C\":\"5%碳酸氢钠溶液\",\"D\":\"山梨醇\",\"E\":\"水解蛋白液\"},\"answer\":\"B\",\"desc\":\"晶体溶液包括：①葡萄糖溶液，如5%葡萄糖溶液及10%葡萄糖溶液，供给水分和热能。②等渗电解质溶液，如0.9%氯化钠、5%葡萄糖氯化钠和复方氯化钠等，供给水、电解质。③碱性溶液，如5%碳酸氢钠和11.2%乳酸钠，纠正酸中毒，调节酸碱平衡。④高渗溶液，如20%甘露醇、25%山梨醇和25%～50%葡萄糖溶液，可利尿脱水。\"}');
INSERT INTO `mgy_exam_ques` VALUES (184, '{\"title\":\"可供给病人水分和热量的溶液是\",\"option\":{\"A\":\"5%～10%葡萄糖注射液\",\"B\":\"0.9%氯化钠\",\"C\":\"50%葡萄糖注射液\",\"D\":\"水解蛋白\",\"E\":\"各种代血浆\"},\"answer\":\"A\",\"desc\":\"葡萄糖溶液包括5%葡萄糖溶液及10%葡萄糖溶液，供给水分和热能。\"}');
INSERT INTO `mgy_exam_ques` VALUES (185, '{\"title\":\"溶血反应第二阶段的典型症状是\",\"option\":{\"A\":\"头胀，四肢麻木\",\"B\":\"腰背部剧痛，血压下降\",\"C\":\"黄疸，血红蛋白尿\",\"D\":\"少尿或无尿\",\"E\":\"寒战发热\"},\"answer\":\"C\",\"desc\":\"溶血反应中间阶段：继而出现黄疸和血红蛋白尿，同时伴有寒战、高热、呼吸急促和血压下降等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (186, '{\"title\":\"空气栓塞致死的原因是气栓阻塞\",\"option\":{\"A\":\"肺静脉入口\",\"B\":\"肺动脉入口\",\"C\":\"上腔静脉入口\",\"D\":\"下腔静脉入口\",\"E\":\"主动脉入口\"},\"answer\":\"B\",\"desc\":\"空气进入静脉，随血循环到达右心房、右心室。如空气量少可被右心室压入肺动脉，并分散进入肺小动脉，最后经肺毛细血管吸收，损害较小。如空气量大，在右心室阻塞肺动脉入口，使血液不能进入肺内，导致机体严重缺氧，甚至引起病人死亡。\"}');
INSERT INTO `mgy_exam_ques` VALUES (187, '{\"title\":\"做头皮静脉输液时，下列哪项不妥\",\"option\":{\"A\":\"备好所需的用物及台灯\",\"B\":\"备好头皮针和等渗盐水注射器\",\"C\":\"操作者应立于患儿头端操作\",\"D\":\"局部应用2%碘酊和70%乙醇严格消毒\",\"E\":\"进入静脉后先固定再调节好滴速\"},\"answer\":\"D\",\"desc\":\"头皮静脉输液最好用75%酒精消毒，因为血管本身就不好找，用碘伏消毒有着色，更加不好寻找血管。\"}');
INSERT INTO `mgy_exam_ques` VALUES (188, '{\"title\":\"为增强肠瘘病人的抵抗力，可输入\",\"option\":{\"A\":\"5%葡萄糖溶液\",\"B\":\"低分子右旋糖酐\",\"C\":\"中分子右旋糖酐\",\"D\":\"5%碳酸氢钠溶液\",\"E\":\"水解蛋白\"},\"answer\":\"E\",\"desc\":\"水解蛋白注射液可以补充蛋白质，纠正低蛋白血症，促进组织修复，用于低蛋白血症、营养不良。\"}');
INSERT INTO `mgy_exam_ques` VALUES (189, '{\"title\":\"急性肺水肿的下列处理中哪项不妥\",\"option\":{\"A\":\"停止输液\",\"B\":\"安置端坐位，两腿下垂\",\"C\":\"氧流量调节至1～2L/min给氧\",\"D\":\"吸入氧气经20%、30%乙醇湿化\",\"E\":\"用镇静、强心、扩血管药物\"},\"answer\":\"C\",\"desc\":\"护理措施：①预防：严格控制输液滴速和输液量，对心肺疾患、老年人、儿童尤为重要。②立即停止输液，及时通知医生，配合抢救。③使病人取端坐位，两腿下垂以减少静脉回流，减轻心脏负荷。④给予高流量吸氧，使肺泡内压力增加，减少肺泡内毛细血管渗出液的产生，同时给予20%～30%乙醇湿化吸氧。乙醇有降低肺泡内泡沫表面张力的作用，使泡沫破裂，改善肺部的气体交换及缺氧状态。⑤按医嘱给予血管扩张剂、平喘药、强心剂、利尿剂等。⑥必要时进行四肢轮流结扎：止血带以阻断静脉血流，每隔5～10min放松一侧肢体的止血带，可有效地减少静脉回心血量，缓解肺水肿症状。待症状缓解后，逐步解除止血带。\"}');
INSERT INTO `mgy_exam_ques` VALUES (190, '{\"title\":\"肺水肿病人湿化吸氧宜用\",\"option\":{\"A\":\"70%乙醇\",\"B\":\"20%～30%乙醇\",\"C\":\"50%硫酸镁\",\"D\":\"生理盐水\",\"E\":\"普鲁卡因\"},\"answer\":\"B\",\"desc\":\"肺水肿病人给予20%～30%乙醇湿化吸氧。乙醇有降低肺泡内泡沫表面张力的作用，使泡沫破裂，改善肺部的气体交换及缺氧状态。\"}');
INSERT INTO `mgy_exam_ques` VALUES (191, '{\"title\":\"液体不滴时注射部位无肿胀、疼痛，有回血，其原因是\",\"option\":{\"A\":\"针头滑出在血管外\",\"B\":\"针头斜面一半在血管内，一半在外\",\"C\":\"针头阻塞\",\"D\":\"输液时间过长\",\"E\":\"针头的斜面紧贴血管壁\"},\"answer\":\"E\",\"desc\":\"溶液不滴的原因：①针头滑出静脉外。液体注入皮下组织，局部有肿胀、疼痛，应另选血管重新穿刺。②针头斜面紧贴血管壁。可调整针头位置或适当变换肢体位置，直到滴注通畅为止。③针头阻塞。折叠滴管下输液管，同时挤压近针头端的输液管；若感觉有阻力，且无回血，则表示针头已阻塞，应更换针头重新穿刺。④压力过低。可抬高输液瓶位置。⑤静脉痉挛。可用热水袋或热毛巾热敷注射部位上端血管，以缓解静脉痉挛。\"}');
INSERT INTO `mgy_exam_ques` VALUES (192, '{\"title\":\"贫血病人宜输注\",\"option\":{\"A\":\"新鲜血\",\"B\":\"库血\",\"C\":\"红细胞\",\"D\":\"新鲜血浆\",\"E\":\"水解蛋白\"},\"answer\":\"C\",\"desc\":\"红细胞，分浓缩红细胞和洗涤红细胞。浓缩红细胞（比容红细胞）：用于血容量正常而需补充红细胞的贫血病人，分离后应在24h内使用。洗涤红细胞：红细胞经生理盐水三次洗涤后再加入适量生理盐水，适用于贫血和一氧化碳中毒的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (193, '{\"title\":\"输液过程中，病人咳大量粉红色泡沫痰，应立即给其取\",\"option\":{\"A\":\"平卧位\",\"B\":\"侧卧位\",\"C\":\"俯卧位\",\"D\":\"半卧位\",\"E\":\"端坐位，两腿下垂\"},\"answer\":\"E\",\"desc\":\"急性肺水肿，输液过程中病人突然出现胸闷、气促、咳嗽，咯粉红色泡沫样痰，严重时可有泡沫样血痰自口鼻涌出，肺部听诊可闻及大量湿啰音。心率快，心律不齐。使病人取端坐位，两腿下垂以减少静脉回流，减轻心脏负荷。\"}');
INSERT INTO `mgy_exam_ques` VALUES (194, '{\"title\":\"一氧化碳中毒病人宜输入\",\"option\":{\"A\":\"血浆\",\"B\":\"白蛋白液\",\"C\":\"新鲜血\",\"D\":\"库血\",\"E\":\"红细胞混悬液\"},\"answer\":\"E\",\"desc\":\"冼涤红细胞，红细胞经生理盐水三次洗涤后再加入适量生理盐水，适用于贫血和一氧化碳中毒的病人。\"}');
INSERT INTO `mgy_exam_ques` VALUES (195, '{\"title\":\"库血的有效保存期是\",\"option\":{\"A\":\"7～10日\",\"B\":\"14～21日\",\"C\":\"25～30日\",\"D\":\"40日\",\"E\":\"50～60日\"},\"answer\":\"B\",\"desc\":\"库存血仅保留红细胞及血浆蛋白。在4℃冰箱内冷藏，保存2～3周。库存血保存时间越长，血液成分变化越大，即酸性增高，钾离子浓度增高，故大量输注库存血时，要警惕酸中毒与高钾血症的发生。库存血适用于各种原因引起的大出血。\"}');
INSERT INTO `mgy_exam_ques` VALUES (196, '{\"title\":\"溶血反应时出现黄疸和血红蛋白尿的机制是\",\"option\":{\"A\":\"红细胞凝集成团，阻塞部分小血管\",\"B\":\"凝集的红细胞发生溶解，大量血红蛋白释放入血浆\",\"C\":\"血红蛋白变成结晶体，阻塞肾小管\",\"D\":\"肾小管皮质缺血、缺氧而坏死脱落\",\"E\":\"红细胞破坏释放凝血物质引起DIC\"},\"answer\":\"B\",\"desc\":\"由于血液中红细胞凝集成团，继而发生溶解，大量血红蛋白散布到血浆中而导致机体发生一系列反应，为最严重的一种输血反应。\"}');
INSERT INTO `mgy_exam_ques` VALUES (197, '{\"title\":\"关于输液速度的调节，下列哪项是错误的\",\"option\":{\"A\":\"年老体弱的病人宜慢\",\"B\":\"心肺功能不全的病人宜慢\",\"C\":\"升压药宜慢\",\"D\":\"5%葡萄糖宜快\",\"E\":\"严重脱水的病人宜快\"},\"answer\":\"D\",\"desc\":\"调节输液速度的原则：①根据病人的年龄、病情、药物性质进行调节，一般成人40～60gtt/min，儿童20～40gtt/min。②对年老、体弱、婴幼儿、有心肺疾患的病人输入速度宜慢；对严重脱水、心肺功能良好的病人输液速度适当加快。③一般输液速度可稍快；而高渗盐水、含钾药物、升压药物等输入速度宜慢。\"}');
INSERT INTO `mgy_exam_ques` VALUES (198, '{\"title\":\"颈外静脉穿刺的正确部位是\",\"option\":{\"A\":\"锁骨下缘中点，颈外静脉外侧缘\",\"B\":\"下颌角和锁骨上缘中点连线上1/2处\",\"C\":\"下颌角和锁骨上缘中点连线下1/2处\",\"D\":\"下颌角和锁骨上缘中点连线上1/3处\",\"E\":\"下颌角和锁骨上缘中点连线下1/3处\"},\"answer\":\"D\",\"desc\":\"颈外静脉穿刺部位在下颌角与锁骨上缘中点连线之上1/3处，颈外静脉外缘处进针。\"}');
INSERT INTO `mgy_exam_ques` VALUES (199, '{\"title\":\"输液发生肺水肿，让病人采取端坐位，双腿下垂并进行四肢轮流结扎，其主要目的是\",\"option\":{\"A\":\"减少肺泡内毛细血管漏出液的产生\",\"B\":\"减少静脉回心血量\",\"C\":\"改善缺氧症状\",\"D\":\"使病人舒适\",\"E\":\"改善末梢血液循环\"},\"answer\":\"B\",\"desc\":\"使病人取端坐位，两腿下垂，以减少静脉回流，减轻心脏负荷。\"}');
INSERT INTO `mgy_exam_ques` VALUES (200, '{\"title\":\"输液引起发热反应的常见原因包括\",\"option\":{\"A\":\"输入的液体量过多\",\"B\":\"输液的压力过大\",\"C\":\"输液的时间过\",\"D\":\"输入致过敏的物质\",\"E\":\"输入致热的物质\"},\"answer\":\"E\",\"desc\":\"发热反应的发生主要是由于输入致热物质，如致热原、死菌等，药物成分不纯、输液用具污染等均可引起。与输液的时间、输液量、输液压力无直接关系。输入致过敏的物质是引起过敏反应的直接原因。\"}');
INSERT INTO `mgy_exam_ques` VALUES (201, '{\"title\":\"留取24h尿标本做内分泌系统检查，应选用那种防腐剂\",\"option\":{\"A\":\"甲苯\",\"B\":\"浓盐酸\",\"C\":\"甲醛\",\"D\":\"10%福尔马林\",\"E\":\"麝香草酚\"},\"answer\":\"B\",\"desc\":\"24h尿中共加浓盐酸5～10ml，保持尿液在酸性环境中，防止尿中激素被氧化。用于内分泌系统的检验，如17-酮类固醇、17-羟类固醇。\"}');
INSERT INTO `mgy_exam_ques` VALUES (202, '{\"title\":\"留取中段尿，主要检查\",\"option\":{\"A\":\"糖\",\"B\":\"红细胞\",\"C\":\"蛋白\",\"D\":\"肌酐、肌酸\",\"E\":\"细菌\"},\"answer\":\"E\",\"desc\":\"留取中段尿主要检查细菌。\"}');
INSERT INTO `mgy_exam_ques` VALUES (203, '{\"title\":\"最严重的输血反应是\",\"option\":{\"A\":\"空气栓塞\",\"B\":\"过敏反应\",\"C\":\"溶血反应\",\"D\":\"循环负荷过重\",\"E\":\"枸橼酸钠中毒\"},\"answer\":\"C\",\"desc\":\"溶血反应为一系列输血反应中最严重的反应，后期出现急性肾功能衰竭，迅速死亡。\"}');
INSERT INTO `mgy_exam_ques` VALUES (204, '{\"title\":\"做艾迪计数时尿标本中加甲醛的作用是\",\"option\":{\"A\":\"固定尿中的有机成分\",\"B\":\"防止尿液改变颜色\",\"C\":\"保持尿液化学成分不变\",\"D\":\"防止尿中激素被氧化\",\"E\":\"避免尿液被污染\"},\"answer\":\"A\",\"desc\":\"每30ml尿液加40%甲醛液1滴，防止细菌生长和固定尿中有机成分。用于做尿艾迪计数（12h尿细胞计数）等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (205, '{\"title\":\"有关输血前准备工作，下列描述错误的是\",\"option\":{\"A\":\"禁止同时为两人采集血标本\",\"B\":\"必须两人核对，严格三查七对\",\"C\":\"分界不清的库血不能使用\",\"D\":\"库血应在室温内放置20min再输入\",\"E\":\"输血前将血浆以旋转轻轻摇匀\"},\"answer\":\"B\",\"desc\":\"护士在采血及输血前应两人严格三查八对；库血取出后不能加热，以免血浆蛋白凝固变性，可在室温下放置15～20min再输入，如30min内不能输入，则应置冰箱冷藏（2～4℃）；输血前不能剧烈振荡，或上下颠倒摇匀，以免破坏红细胞，可以旋转轻轻摇匀；正常库血上层血浆为黄色，下层血细胞呈红色，两者之间界限清楚，分界不清提示血液变质，不能输入；同时为两人采集血标本容易引起混淆。\"}');
INSERT INTO `mgy_exam_ques` VALUES (206, '{\"title\":\"查阿米巴原虫，留取粪便标本的正确方法是\",\"option\":{\"A\":\"清晨留取少许\",\"B\":\"留新鲜粪便，注意保暖，立即送检\",\"C\":\"取粪便表面的部分\",\"D\":\"取粪便的不同部位\",\"E\":\"取粪便异常部位\"},\"answer\":\"B\",\"desc\":\"检查阿米巴原虫时，将便盆加温至接近病人的体温，标本在30min内连同便盆送检，保持阿米巴原虫的活动状态。\"}');
INSERT INTO `mgy_exam_ques` VALUES (207, '{\"title\":\"尿常规检查应在何时留取标本最合适\",\"option\":{\"A\":\"饭前半小时\",\"B\":\"全天尿液\",\"C\":\"早晨第一次尿\",\"D\":\"随时收集尿液\",\"E\":\"饭后半小时\"},\"answer\":\"C\",\"desc\":\"可下床活动的病人，给予标本容器，嘱病人留取晨尿50～100ml。\"}');
INSERT INTO `mgy_exam_ques` VALUES (208, '{\"title\":\"下列哪项不是留取24h尿标本的目的\",\"option\":{\"A\":\"检查尿中的钾钠氯\",\"B\":\"做尿糖定量或尿浓缩试验\",\"C\":\"做细菌学检查\",\"D\":\"做尿17-羟类固醇、17-酮类固醇检查\",\"E\":\"检查结核杆菌\"},\"answer\":\"D\",\"desc\":\"12h或24h尿标本的目的是用做各种尿生化检验或尿浓缩查结核杆菌等检查。\"}');
INSERT INTO `mgy_exam_ques` VALUES (209, '{\"title\":\"生化检验的血标本应在什么时候采集\",\"option\":{\"A\":\"清晨空腹\",\"B\":\"饭前半小时\",\"C\":\"饭后半小时\",\"D\":\"临睡前\",\"E\":\"没有时间限制\"},\"answer\":\"A\",\"desc\":\"生化标本应清晨空腹采集，避免因进食而影响检验结果。\"}');
INSERT INTO `mgy_exam_ques` VALUES (210, '{\"title\":\"关于给氧的适应证，下列哪项不妥\",\"option\":{\"A\":\"气胸\",\"B\":\"肺水肿\",\"C\":\"安眠药中毒\",\"D\":\"急性胃炎\",\"E\":\"哮喘\"},\"answer\":\"D\",\"desc\":\"吸氧适应证：①呼吸系统疾患。肺活量减少因呼吸系统疾患而影响肺活量者。②心功能不全。心肺功能不全使肺部充血而致呼吸困难者。③各种中毒引起的呼吸困难。④昏迷病人。⑤其他：某些外科手术前后、大出血休克的病人以及分娩时产程过长或胎心音不良等。\"}');
INSERT INTO `mgy_exam_ques` VALUES (211, '{\"title\":\"哪项不是一般情况观察内容\",\"option\":{\"A\":\"意识状态\",\"B\":\"面部表情\",\"C\":\"皮肤黏膜\",\"D\":\"睡眠饮食\",\"E\":\"姿势体位\"},\"answer\":\"A\",\"desc\":\"一般情况：①表情与面容。如精神状态、面色。②饮食与营养。如饮食量及有无消耗性体征。③姿势与体位。如有无强迫体位及肌肉、关节的异常。④皮肤与黏膜。如皮肤弹性、颜色、温度、湿度、完整性、有无皮疹及出血点或淤斑等。⑤休息与睡眠。⑥呕吐物。注意呕吐物的颜色、性质、量和次数。⑦排泄物。\"}');
INSERT INTO `mgy_exam_ques` VALUES (212, '{\"title\":\"应给病人吸氧的动脉血氧分压标准是\",\"option\":{\"A\":\"10.6kPa\",\"B\":\"9.6kPa\",\"C\":\"8.6kPa\",\"D\":\"7.6kPa\",\"E\":\"6.6kPa\"},\"answer\":\"E\",\"desc\":\"脑的耗氧量占全身耗氧量的1/5，脑组织对缺氧的耐受性很差，正常情况下，动脉血氧分压为10.6～13.3kPa，缺氧时，中枢神经系统症状出现最早，PaO2<6.6kPa的病人，必须给予氧气治疗。\"}');
INSERT INTO `mgy_exam_ques` VALUES (213, '{\"title\":\"氧气筒的体积一般为\",\"option\":{\"A\":\"100L\",\"B\":\"80L\",\"C\":\"60L\",\"D\":\"40L\",\"E\":\"20L\"},\"answer\":\"D\",\"desc\":\"氧气筒的体积一般为40L。\"}');
INSERT INTO `mgy_exam_ques` VALUES (214, '{\"title\":\"氧浓度高于多少时，持续时间超过1～2天，则会发生氧中毒\",\"option\":{\"A\":\"55%\",\"B\":\"60%\",\"C\":\"65%\",\"D\":\"70%\",\"E\":\"75%\"},\"answer\":\"D\",\"desc\":\"持续给予高于70%的氧浓度，时间超过1～2天，则会发生氧中毒。\"}');
INSERT INTO `mgy_exam_ques` VALUES (215, '{\"title\":\"鼻导管给氧法操作正确的是\",\"option\":{\"A\":\"给氧前用干棉签清洁病人鼻孔\",\"B\":\"导管插入长度为鼻尖到耳垂的1/2\",\"C\":\"给氧时，调节氧流量后插入鼻导管\",\"D\":\"停止给氧时，应先关氧气开关\",\"E\":\"氧气筒入置距暖气应0.5m\"},\"answer\":\"C\",\"desc\":\"给氧时，用湿棉签清洁鼻孔，连接鼻导管，开流量调节阀，确定氧气流出通畅后，调节所需氧流量，将鼻导管蘸水，从一侧鼻腔插入鼻尖至耳垂的2/3长度，停用氧气时，先拔出鼻导管，然后关总开关，无余气时再关流量调节阀。使用氧气时，应做到“四防”，氧气筒至少距火炉5m，暖气1m。\"}');
INSERT INTO `mgy_exam_ques` VALUES (216, '{\"title\":\"下列不符合吸痰护理操作的是\",\"option\":{\"A\":\"插管前应检查导管是否通畅\",\"B\":\"吸痰前对缺氧严重者应加大氧流量\",\"C\":\"每次吸痰时间不超过15s\",\"D\":\"痰液黏稠时滴入少量生理盐水稀释\",\"E\":\"吸痰导管每日更换1～2次\"},\"answer\":\"E\",\"desc\":\"治疗盘内吸痰用物应每日更换1～2次，吸痰导管每次用后更换。\"}');
INSERT INTO `mgy_exam_ques` VALUES (217, '{\"title\":\"洗胃时，一次洗胃液灌入量应不超过\",\"option\":{\"A\":\"100ml\",\"B\":\"200ml\",\"C\":\"300ml\",\"D\":\"400ml\",\"E\":\"500ml\"},\"answer\":\"E\",\"desc\":\"洗胃液一次灌入流量300～500ml，过多则胃容积增大，可使胃内压上升，导致急性胃扩张，刺激迷走神经兴奋，引起反射性心脏骤停。同时，毒物快速排入肠道，增加毒物吸收量。另外，过多也可引起液体反流，导致呛咳、误吸或窒息。\"}');
INSERT INTO `mgy_exam_ques` VALUES (218, '{\"title\":\"电动吸引洗胃法洗胃的原理是\",\"option\":{\"A\":\"虹吸\",\"B\":\"置换\",\"C\":\"空吸\",\"D\":\"负压\",\"E\":\"渗透\"},\"answer\":\"D\",\"desc\":\"电动吸引洗胃法洗胃是利用正压冲洗和负压吸引，迅速、有效地清除胃内毒物的方法。\"}');
INSERT INTO `mgy_exam_ques` VALUES (219, '{\"title\":\"除哪项外均为濒死的表现\",\"option\":{\"A\":\"心肌收缩无力、心脏搏出量少，血压下降\",\"B\":\"血液循环迟缓，微循环障碍\",\"C\":\"体温调节中枢功能紊乱\",\"D\":\"重要器官灌注不足，出现脑缺氧的一系列症状\",\"E\":\"呼吸深而慢，瞳孔缩小\"},\"answer\":\"E\",\"desc\":\"濒死期：机体各系统的功能严重紊乱，中枢神经系统脑干以上功能处于抑制状态，肌张力消失，呼吸急促困难，出现陈—施呼吸，脉搏不规则且快而弱，血压降低或测不到。\"}');
INSERT INTO `mgy_exam_ques` VALUES (220, '{\"title\":\"进行尸体料理，下列哪项不妥\",\"option\":{\"A\":\"放低头部\",\"B\":\"合上眼睑\",\"C\":\"必要时用绷带托扶下颌\",\"D\":\"填塞孔道\",\"E\":\"擦净尸体\"},\"answer\":\"A\",\"desc\":\"将床放平，使尸体仰卧，头下垫一枕，以防面部变色，用大单遮盖尸体。\"}');
INSERT INTO `mgy_exam_ques` VALUES (221, '{\"title\":\"交班报告一般应由何人书写\",\"option\":{\"A\":\"由护士长书写\",\"B\":\"由值班护士书写\",\"C\":\"由高年资护士书写\",\"D\":\"由年轻护士书写\",\"E\":\"由实习护士书写\"},\"answer\":\"B\",\"desc\":\"交班报告一般应由值班护士书写。\"}');
INSERT INTO `mgy_exam_ques` VALUES (222, '{\"title\":\"日间用蓝钢笔、夜间用红钢笔书写的表格有\",\"option\":{\"A\":\"体温单\",\"B\":\"医嘱单\",\"C\":\"病区报告\",\"D\":\"病程记录\",\"E\":\"入院记录\"},\"answer\":\"C\",\"desc\":\"值班护士必须认真负责，深入病室，全面了解病人一般情况，掌握新病人、危重病人的病情动态和治疗方案等，并填写病区报告。早班用蓝色钢笔记录，中班和夜班用红色钢笔记录。\"}');
INSERT INTO `mgy_exam_ques` VALUES (223, '{\"title\":\"不在体温单40～42℃相应时间内标明的是\",\"option\":{\"A\":\"化疗时间\",\"B\":\"入院时间\",\"C\":\"转科时间\",\"D\":\"分娩时间\",\"E\":\"手术时间\"},\"answer\":\"A\",\"desc\":\"化疗时间不在体温单40～42℃相应时间内标明。\"}');
INSERT INTO `mgy_exam_ques` VALUES (224, '{\"title\":\"下列哪项不是住院病案的内容\",\"option\":{\"A\":\"护理记录及护理病历\",\"B\":\"医疗记录\",\"C\":\"各种证明文件\",\"D\":\"住院检查记录\",\"E\":\"门诊治疗记录\"},\"answer\":\"E\",\"desc\":\"门诊治疗记录不是住院病案的内容。\"}');
INSERT INTO `mgy_exam_ques` VALUES (225, '{\"title\":\"禁用鼻饲法的患者是\",\"option\":{\"A\":\"食道静脉曲张者\",\"B\":\"拒绝进食者\",\"C\":\"昏迷者\",\"D\":\"早产儿\",\"E\":\"口腔疾患\"},\"answer\":\"A\",\"desc\":\"凡上消化道出血、食管静脉曲张或梗阻，以及鼻腔手术后的病人禁用鼻饲法。\"}');
INSERT INTO `mgy_exam_ques` VALUES (226, '{\"title\":\"男，43岁，患慢性胆囊炎，护士嘱咐病人应用的饮食是\",\"option\":{\"A\":\"低盐\",\"B\":\"低蛋白\",\"C\":\"低脂肪\",\"D\":\"低糖\",\"E\":\"低碳水化合物\"},\"answer\":\"C\",\"desc\":\"低脂肪饮食适用范围：适用于肝胆胰疾病的病人，以及高脂血症、动脉硬化、冠心病、肥胖症及腹泻病人。\"}');
INSERT INTO `mgy_user` VALUES (1, '0F0B0F6D53467C5083DBCFE702E45388', 'chenym');
